 57	        /// <summary>
 58	        /// Enable pet service provider mode for the current user
 59	        /// </summary>
 60	        [HttpPost("enable-provider-mode")]
 61	        public async Task<IActionResult> EnableProviderMode([FromBody] EnableProviderModeDto request)
 62	        {
 63	            try
 64	            {
 65	                var userId = User.FindFirst(ClaimTypes.NameIdentifier)?.Value;
 66	                if (string.IsNullOrEmpty(userId))
 67	                    return Unauthorized();
 68	
 69	                // Assign PetService role to user
 70	                await _roleManagementService.AddUserToRoleAsync(userId, "PetServiceProvider");
 71	
 72	                var profile = await _petServiceService.CreateOrUpdateProfileAsync(userId, request);
 73	                
 74	                // Geocode the postcode to get coordinates if profile was created/updated successfully
 75	                if (profile != null && profile.Id > 0)
 76	                {
 77	                    _logger.LogInformation("Geocoding pet service profile {ProfileId} for user {UserId}", 
 78	                        profile.Id, userId);
 79	                    await _locationGeocodingService.UpdatePetServiceProfileCoordinatesAsync(profile.Id);
 80	                }
 81	                
 82	                return Ok(profile);
 83	            }
 84	            catch (Exception ex)
 85	            {
 86	                _logger.LogError(ex, "Error enabling pet service provider mode");
 87	                return StatusCode(500, "An error occurred while enabling pet service provider mode");
 88	            }
 89	        }
