131	        /// <summary>
132	        /// Search for pet service providers with optional availability data
133	        /// </summary>
134	        [HttpGet("search")]
135	        [AllowAnonymous]
136	        public async Task<IActionResult> SearchProviders(
137	            [FromQuery] string? location = null,
138	            [FromQuery] int? radius = null,
139	            [FromQuery] List<PetServiceType>? serviceTypes = null,
140	            [FromQuery] DateTime? startDate = null,
141	            [FromQuery] DateTime? endDate = null,
142	            [FromQuery] string? sortBy = null,
143	            [FromQuery] int pageNumber = 1,
144	            [FromQuery] int pageSize = 10,
145	            [FromQuery] bool includeAvailability = false)
146	        {
147	            var searchParams = new ProviderSearchParams
148	            {
149	                Location = location,
150	                Radius = radius,
151	                ServiceTypes = serviceTypes,
152	                StartDate = startDate,
153	                EndDate = endDate,
154	                SortBy = sortBy,
155	                PageNumber = pageNumber,
156	                PageSize = pageSize
157	            };
158	
159	            // Check if we should include availability data
160	            if (includeAvailability)
161	            {
162	                // Convert to SearchPetServicesRequest for the new method
163	                var request = new SearchPetServicesRequest
164	                {
165	                    Location = location,
166	                    Radius = radius,
167	                    ServiceTypes = serviceTypes?.Select(st => (int)st).ToList() ?? new List<int>(),
168	                    FromDate = startDate,
169	                    ToDate = endDate,
170	                    SortBy = sortBy ?? "Distance",
171	                    PageNumber = pageNumber,
172	                    PageSize = pageSize
173	                };
174	                
175	                var resultsWithAvailability = await _petServiceService.SearchProvidersWithAvailabilityAsync(request);
176	                return Ok(resultsWithAvailability);
177	            }
178	            else
179	            {
180	                var results = await _petServiceService.SearchProvidersAsync(searchParams);
181	                return Ok(results);
182	            }
183	        }
