331	        /// <summary>
332	        /// Get KYC status for the current pet service provider
333	        /// </summary>
334	        [HttpGet("my-kyc-status")]
335	        public async Task<IActionResult> GetMyKycStatus()
336	        {
337	            var userId = User.FindFirst(ClaimTypes.NameIdentifier)?.Value;
338	            if (string.IsNullOrEmpty(userId))
339	                return Unauthorized();
340	
341	            // Development mode bypass
342	            if (_configuration.GetValue<bool>("KycSettings:BypassInDevelopment", false) && 
343	                _environment.IsDevelopment())
344	            {
345	                _logger.LogWarning("KYC status check bypassed in development mode for user {UserId}", userId);
346	                
347	                return Ok(new
348	                {
349	                    isVerified = true,
350	                    KycStatus = "Verified",
351	                    KycVerifiedDate = DateTime.UtcNow.AddDays(-1),
352	                    KycExpiryDate = DateTime.UtcNow.AddYears(1),
353	                    BackgroundCheckStatus = "Passed",
354	                    BackgroundCheckDate = DateTime.UtcNow.AddDays(-1)
355	                });
356	            }
357	
358	            var profile = await _context.PetServiceProfiles
359	                .Where(p => p.UserId == userId)
360	                .Select(p => new 
361	                {
362	                    p.KycStatus,
363	                    p.KycVerifiedDate,
364	                    p.KycExpiryDate,
365	                    p.BackgroundCheckStatus,
366	                    p.BackgroundCheckDate
367	                })
368	                .FirstOrDefaultAsync();
369	
370	            if (profile == null)
371	                return NotFound("Pet service provider profile not found");
372	
373	            // Add isVerified flag for consistency
374	            return Ok(new
375	            {
376	                isVerified = profile.KycStatus == "Verified",
377	                profile.KycStatus,
378	                profile.KycVerifiedDate,
379	                profile.KycExpiryDate,
380	                profile.BackgroundCheckStatus,
381	                profile.BackgroundCheckDate
382	            });
383	        }
