499	        /// <summary>
500	        /// Get full pricing information for a specific provider
501	        /// </summary>
502	        [HttpGet("providers/{userId}/full-pricing")]
503	        [AllowAnonymous]
504	        public async Task<IActionResult> GetProviderFullPricing(string userId)
505	        {
506	            try
507	            {
508	                _logger.LogInformation("GetProviderFullPricing called for user {UserId}", userId);
509	                
510	                // Get the pet service profile with related data
511	                var profile = await _context.PetServiceProfiles
512	                    .Include(p => p.User)
513	                    .Include(p => p.SubServices)
514	                        .ThenInclude(ss => ss.SubServiceType)
515	                    .Include(p => p.SubServices)
516	                        .ThenInclude(ss => ss.Rate)
517	                    .AsNoTracking()  // Add this for better performance
518	                    .FirstOrDefaultAsync(p => p.UserId == userId);
519	                    
520	                if (profile == null)
521	                {
522	                    _logger.LogWarning("Provider profile not found for user {UserId}", userId);
523	                    return NotFound("Provider not found");
524	                }
525	                
526	                // Get unique service types from sub-services
527	                var serviceTypes = profile.SubServices
528	                    .Where(ss => ss.IsActive && ss.SubServiceType != null)
529	                    .Select(ss => ss.SubServiceType.ServiceType)
530	                    .Distinct()
531	                    .ToList();
532	                
533	                // Build service rates grouped by service type
534	                var serviceRates = new List<object>();
535	                
536	                foreach (var serviceTypeId in serviceTypes)
537	                {
538	                    var subServicesForType = profile.SubServices
539	                        .Where(ss => ss.IsActive && ss.SubServiceType?.ServiceType == serviceTypeId)
540	                        .ToList();
541	                    
542	                    // Get rates for this service type
543	                    var hourlyRate = subServicesForType
544	                        .FirstOrDefault(ss => ss.Rate?.RateType == PetServiceRateType.Hourly)
545	                        ?.Rate?.Rate;
546	                    
547	                    var dayRate = subServicesForType
548	                        .FirstOrDefault(ss => ss.Rate?.RateType == PetServiceRateType.Daily)
549	                        ?.Rate?.Rate;
550	                    
551	                    var overnightRate = subServicesForType
552	                        .FirstOrDefault(ss => ss.Rate?.RateType == PetServiceRateType.Overnight)
553	                        ?.Rate?.Rate;
554	                    
555	                    var flatRatePerDog = subServicesForType
556	                        .FirstOrDefault(ss => ss.Rate?.RateType == PetServiceRateType.PerDog)
557	                        ?.Rate?.Rate;
558	                    
559	                    serviceRates.Add(new
560	                    {
561	                        serviceType = serviceTypeId,
562	                        serviceTypeName = GetServiceTypeName(serviceTypeId),
563	                        serviceTypeIcon = GetServiceTypeIcon(serviceTypeId),
564	                        hourlyRate = hourlyRate,
565	                        dayRate = dayRate,
566	                        overnightRate = overnightRate,
567	                        flatRatePerDog = flatRatePerDog,
568	                        notes = subServicesForType.FirstOrDefault()?.Description,
569	                        subServices = subServicesForType
570	                            .Where(ss => ss.Rate != null)
571	                            .Select(ss => new
572	                            {
573	                                subServiceType = ss.SubServiceTypeId,
574	                                subServiceName = ss.SubServiceType?.Name ?? "Additional Service",
575	                                description = ss.SubServiceType?.Description,
576	                                rate = ss.Rate?.Rate ?? 0,
577	                                isActive = ss.IsActive,
578	                                duration = ss.SubServiceType?.DefaultDuration
579	                            })
580	                            .ToList()
581	                    });
582	                }
583	                
584	                // Build comprehensive pricing response
585	                var fullPricing = new
586	                {
587	                    providerId = profile.UserId,
588	                    businessName = $"{profile.User?.FirstName} {profile.User?.LastName}".Trim(),
589	                    isFullData = true,
590	                    lastUpdated = profile.ModifiedDate,
591	                    serviceRates = serviceRates,
592	                    additionalCharges = new
593	                    {
594	                        weekendSurcharge = (decimal?)null, // Not available in current model
595	                        holidaySurcharge = (decimal?)null, // Not available in current model
596	                        emergencySurcharge = profile.EmergencySurchargePercent,
597	                        additionalDogCharge = profile.MultiPetDiscount, // This is actually a discount percentage
598	                        multiPetDiscount = profile.MultiPetDiscount
599	                    },
600	                    policies = new
601	                    {
602	                        cancellationPolicy = "Contact provider for cancellation policy", // Not available in current model
603	                        minimumBookingHours = (int?)null, // Not available in current model
604	                        maximumDogs = profile.MaxPetsPerBooking,
605	                        acceptsPuppies = profile.AcceptsSmallDogs,
606	                        acceptsSeniorDogs = profile.HandlesSeniorPets,
607	                        acceptsUntrainedDogs = profile.HandlesAnxiousPets,
608	                        // Additional pet size acceptance
609	                        acceptsSmallDogs = profile.AcceptsSmallDogs,
610	                        acceptsMediumDogs = profile.AcceptsMediumDogs,
611	                        acceptsLargeDogs = profile.AcceptsLargeDogs,
612	                        acceptsGiantDogs = profile.AcceptsGiantDogs,
613	                        maxDogWeight = profile.MaxDogWeight.HasValue 
614	                            ? Math.Round(profile.MaxDogWeight.Value / 2.20462m, 1) // Convert lbs to kg for display
615	                            : (decimal?)null,
616	                        // Medical capabilities
617	                        canAdministerMedication = profile.CanAdministerMedication,
618	                        canManageSpecialDiets = profile.CanManageSpecialDiets,
619	                        canAdministerInsulin = profile.CanAdministerInsulin,
620	                        // Emergency availability
621	                        available24x7 = profile.Available24x7,
622	                        offersEmergencyService = profile.OffersEmergencyService
623	                    }
624	                };
625	                
626	                return Ok(fullPricing);
627	            }
628	            catch (Exception ex)
629	            {
630	                _logger.LogError(ex, "Error retrieving full pricing for provider {UserId}", userId);
631	                return StatusCode(500, "An error occurred while retrieving pricing information");
632	            }
633	        }
