using System.Security.Claims;
using Microsoft.AspNetCore.Authorization;
using Microsoft.AspNetCore.Mvc;
using MeAndMyDog.API.Models.PetSitting;
using MeAndMyDog.API.Services.PetSitting;
using MeAndMyDog.API.Services.Reviews;
using MeAndMyDog.SharedKernel.Enums;
using MeAndMyDog.API.Data;
using Microsoft.EntityFrameworkCore;
using MeAndMyDog.API.Services.Interfaces;
using MeAndMyDog.API.Services;
using Microsoft.Extensions.Configuration;
using Microsoft.AspNetCore.Hosting;
using MeAndMyDog.API.Entities.PetSitting;
using Asp.Versioning;

namespace MeAndMyDog.API.Controllers
{
    /// <summary>
    /// API controller for managing pet service provider profiles
    /// </summary>
    [ApiController]
    [ApiVersion("1.0")]
    [Route("api/v{version:apiVersion}/[controller]")]
    [Authorize]
    public class PetServicesController : ControllerBase
    {
        private readonly IPetServiceProfileService _petServiceService;
        private readonly IPetServiceAvailabilityService _availabilityService;
        private readonly ApplicationDbContext _context;
        private readonly IRoleManagementService _roleManagementService;
        private readonly ILogger<PetServicesController> _logger;
        private readonly IConfiguration _configuration;
        private readonly IWebHostEnvironment _environment;
        private readonly ILocationGeocodingService _locationGeocodingService;

        public PetServicesController(
            IPetServiceProfileService petServiceService,
            IPetServiceAvailabilityService availabilityService,
            ApplicationDbContext context,
            IRoleManagementService roleManagementService,
            ILogger<PetServicesController> logger,
            IConfiguration configuration,
            IWebHostEnvironment environment,
            ILocationGeocodingService locationGeocodingService)
        {
            _petServiceService = petServiceService;
            _availabilityService = availabilityService;
            _context = context;
            _roleManagementService = roleManagementService;
            _logger = logger;
            _configuration = configuration;
            _environment = environment;
            _locationGeocodingService = locationGeocodingService;
        }

        /// <summary>
        /// Enable pet service provider mode for the current user
        /// </summary>
        [HttpPost("enable-provider-mode")]
        public async Task<IActionResult> EnableProviderMode([FromBody] EnableProviderModeDto request)
        {
            try
            {
                var userId = User.FindFirst(ClaimTypes.NameIdentifier)?.Value;
                if (string.IsNullOrEmpty(userId))
                    return Unauthorized();

                // Assign PetService role to user
                await _roleManagementService.AddUserToRoleAsync(userId, "PetServiceProvider");

                var profile = await _petServiceService.CreateOrUpdateProfileAsync(userId, request);

                // Geocode the postcode to get coordinates if profile was created/updated successfully
                if (profile != null && profile.Id > 0)
                {
                    _logger.LogInformation("Geocoding pet service profile {ProfileId} for user {UserId}", 
                        profile.Id, userId);
                    await _locationGeocodingService.UpdatePetServiceProfileCoordinatesAsync(profile.Id);
                }

                return Ok(profile);
            }
            catch (Exception ex)
            {
                _logger.LogError(ex, "Error enabling pet service provider mode");
                return StatusCode(500, "An error occurred while enabling pet service provider mode");
            }
        }

        /// <summary>
        /// Get the current user's pet service provider profile
        /// </summary>
        [HttpGet("my-profile")]
        public async Task<IActionResult> GetMyProfile()
        {
            var userId = User.FindFirst(ClaimTypes.NameIdentifier)?.Value;
            if (string.IsNullOrEmpty(userId))
                return Unauthorized();

            var profile = await _petServiceService.GetProfileByUserIdAsync(userId);
            if (profile == null)
                return NotFound("Pet service provider profile not found");

            return Ok(profile);
        }

        /// <summary>
        /// Update the current user's pet service provider profile
        /// </summary>
        [HttpPut("my-profile")]
        public async Task<IActionResult> UpdateMyProfile([FromBody] UpdateProviderProfileDto request)
        {
            var userId = User.FindFirst(ClaimTypes.NameIdentifier)?.Value;
            if (string.IsNullOrEmpty(userId))
                return Unauthorized();

            var profile = await _petServiceService.UpdateProfileAsync(userId, request);

            // Geocode the postcode to get coordinates after update
            if (profile != null && profile.Id > 0)
            {
                _logger.LogInformation("Geocoding pet service profile {ProfileId} after update", 
                    profile.Id);
                await _locationGeocodingService.UpdatePetServiceProfileCoordinatesAsync(profile.Id);
            }

            return Ok(profile);
        }

        /// <summary>
        /// Search for pet service providers with optional availability data
        /// </summary>
        [HttpGet("search")]
        [AllowAnonymous]
        public async Task<IActionResult> SearchProviders(
            [FromQuery] string? location = null,
            [FromQuery] int? radius = null,
            [FromQuery] List<PetServiceType>? serviceTypes = null,
            [FromQuery] DateTime? startDate = null,
            [FromQuery] DateTime? endDate = null,
            [FromQuery] string? sortBy = null,
            [FromQuery] int pageNumber = 1,
            [FromQuery] int pageSize = 10,
            [FromQuery] bool includeAvailability = false)
        {
            var searchParams = new ProviderSearchParams
            {
                Location = location,
                Radius = radius,
                ServiceTypes = serviceTypes,
                StartDate = startDate,
                EndDate = endDate,
                SortBy = sortBy,
                PageNumber = pageNumber,
                PageSize = pageSize
            };

            // Check if we should include availability data
            if (includeAvailability)
            {
                // Convert to SearchPetServicesRequest for the new method
                var request = new SearchPetServicesRequest
                {
                    Location = location,
                    Radius = radius,
                    ServiceTypes = serviceTypes?.Select(st => (int)st).ToList() ?? new List<int>(),
                    FromDate = startDate,
                    ToDate = endDate,
                    SortBy = sortBy ?? "Distance",
                    PageNumber = pageNumber,
                    PageSize = pageSize
                };

                var resultsWithAvailability = await _petServiceService.SearchProvidersWithAvailabilityAsync(request);
                return Ok(resultsWithAvailability);
            }
            else
            {
                var results = await _petServiceService.SearchProvidersAsync(searchParams);
                return Ok(results);
            }
        }

        /// <summary>
        /// Get a specific pet service provider's public profile
        /// </summary>
        [HttpGet("{userId}")]
        [AllowAnonymous]
        public async Task<IActionResult> GetProviderProfile(string userId)
        {
            var profile = await _petServiceService.GetPublicProfileAsync(userId);
            if (profile == null)
                return NotFound("Pet service provider not found");

            return Ok(profile);
        }

        /// <summary>
        /// Get a specific pet service provider's public profile by provider ID
        /// </summary>
        [HttpGet("provider/{id}")]
        [AllowAnonymous]
        public async Task<IActionResult> GetProviderById(string id)
        {
            var profile = await _petServiceService.GetPublicProfileAsync(id);
            if (profile == null)
                return NotFound("Pet service provider not found");

            return Ok(profile);
        }

        /// <summary>
        /// Get reviews for a specific pet service provider
        /// </summary>
        [HttpGet("provider/{id}/reviews")]
        [AllowAnonymous]
        public async Task<IActionResult> GetProviderReviews(string id)
        {
            try
            {
                // Check if review service is available
                var reviewService = HttpContext.RequestServices.GetService<IReviewService>();

                if (reviewService != null)
                {
                    var reviews = await reviewService.GetProviderReviewsAsync(id, includeUnverified: false);
                    return Ok(reviews);
                }
                else
                {
                    // Fallback to empty list if review service is not configured
                    var reviews = new List<object>();
                    _logger.LogWarning("Review service not found in dependency injection container");
                    return Ok(reviews);
                }
            }
            catch (Exception ex)
            {
                _logger.LogError(ex, "Error getting provider reviews for provider ID: {ProviderId}", id);
                return StatusCode(500, "An error occurred while fetching provider reviews");
            }
        }

        /// <summary>
        /// Add availability for the current pet service provider
        /// </summary>
        [HttpPost("availability")]
        public async Task<IActionResult> AddAvailability([FromBody] AddAvailabilityDto request)
        {
            var userId = User.FindFirst(ClaimTypes.NameIdentifier)?.Value;
            if (string.IsNullOrEmpty(userId))
                return Unauthorized();

            var availability = await _availabilityService.AddAvailabilityAsync(userId, request);
            return Ok(availability);
        }

        /// <summary>
        /// Get availability for the current pet service provider
        /// </summary>
        [HttpGet("my-availability")]
        public async Task<IActionResult> GetMyAvailability(
            [FromQuery] DateTime? startDate = null,
            [FromQuery] DateTime? endDate = null)
        {
            var userId = User.FindFirst(ClaimTypes.NameIdentifier)?.Value;
            if (string.IsNullOrEmpty(userId))
                return Unauthorized();

            var availability = await _availabilityService.GetAvailabilityAsync(userId, startDate, endDate);
            return Ok(availability);
        }

        /// <summary>
        /// Delete availability for the current pet service provider
        /// </summary>
        [HttpDelete("availability/{id}")]
        public async Task<IActionResult> DeleteAvailability(int id)
        {
            var userId = User.FindFirst(ClaimTypes.NameIdentifier)?.Value;
            if (string.IsNullOrEmpty(userId))
                return Unauthorized();

            await _availabilityService.DeleteAvailabilityAsync(userId, id);
            return NoContent();
        }

        /// <summary>
        /// Get availability for a specific pet service provider
        /// </summary>
        [HttpGet("{userId}/availability")]
        [AllowAnonymous]
        public async Task<IActionResult> GetProviderAvailability(
            string userId,
            [FromQuery] DateTime? startDate = null,
            [FromQuery] DateTime? endDate = null)
        {
            var availability = await _availabilityService.GetAvailabilityAsync(userId, startDate, endDate);
            return Ok(availability);
        }

        /// <summary>
        /// Get statistics for the current pet service provider
        /// </summary>
        [HttpGet("my-stats")]
        public async Task<IActionResult> GetMyStatistics()
        {
            var userId = User.FindFirst(ClaimTypes.NameIdentifier)?.Value;
            if (string.IsNullOrEmpty(userId))
                return Unauthorized();

            var stats = await _petServiceService.GetProviderStatisticsAsync(userId);
            return Ok(stats);
        }

        /// <summary>
        /// Update services offered by the current pet service provider
        /// </summary>
        [HttpPut("my-services")]
        public async Task<IActionResult> UpdateMyServices([FromBody] List<PetServiceType> serviceTypes)
        {
            var userId = User.FindFirst(ClaimTypes.NameIdentifier)?.Value;
            if (string.IsNullOrEmpty(userId))
                return Unauthorized();

            await _petServiceService.UpdateProviderServicesAsync(userId, serviceTypes);
            return NoContent();
        }

        /// <summary>
        /// Get KYC status for the current pet service provider
        /// </summary>
        [HttpGet("my-kyc-status")]
        public async Task<IActionResult> GetMyKycStatus()
        {
            var userId = User.FindFirst(ClaimTypes.NameIdentifier)?.Value;
            if (string.IsNullOrEmpty(userId))
                return Unauthorized();

            // Development mode bypass
            if (_configuration.GetValue<bool>("KycSettings:BypassInDevelopment", false) && 
                _environment.IsDevelopment())
            {
                _logger.LogWarning("KYC status check bypassed in development mode for user {UserId}", userId);

                return Ok(new
                {
                    isVerified = true,
                    KycStatus = "Verified",
                    KycVerifiedDate = DateTime.UtcNow.AddDays(-1),
                    KycExpiryDate = DateTime.UtcNow.AddYears(1),
                    BackgroundCheckStatus = "Passed",
                    BackgroundCheckDate = DateTime.UtcNow.AddDays(-1)
                });
            }

            var profile = await _context.PetServiceProfiles
                .Where(p => p.UserId == userId)
                .Select(p => new 
                {
                    p.KycStatus,
                    p.KycVerifiedDate,
                    p.KycExpiryDate,
                    p.BackgroundCheckStatus,
                    p.BackgroundCheckDate
                })
                .FirstOrDefaultAsync();

            if (profile == null)
                return NotFound("Pet service provider profile not found");

            // Add isVerified flag for consistency
            return Ok(new
            {
                isVerified = profile.KycStatus == "Verified",
                profile.KycStatus,
                profile.KycVerifiedDate,
                profile.KycExpiryDate,
                profile.BackgroundCheckStatus,
                profile.BackgroundCheckDate
            });
        }

        /// <summary>
        /// Get all service types
        /// </summary>
        [HttpGet("service-types")]
        [AllowAnonymous]
        public async Task<IActionResult> GetServiceTypes()
        {
            try
            {
                var serviceTypes = await _context.ServiceTypes
                    .Where(st => st.IsActive)
                    .OrderBy(st => st.DisplayOrder)
                    .Select(st => new
                    {
                        serviceTypeId = st.ServiceTypeId,
                        name = st.Name,
                        description = st.Description,
                        icon = st.Icon
                    })
                    .ToListAsync();

                return Ok(serviceTypes);
            }
            catch (Exception ex)
            {
                _logger.LogError(ex, "Error retrieving service types");
                return StatusCode(500, "An error occurred while retrieving service types");
            }
        }

        /// <summary>
        /// Get sub-service types for a specific service type
        /// </summary>
        [HttpGet("service-types/{serviceTypeId}/sub-services")]
        [AllowAnonymous]
        public async Task<IActionResult> GetSubServiceTypes(int serviceTypeId)
        {
            try
            {
                var subServices = await _context.SubServiceTypes
                    .Where(sst => sst.ServiceType == serviceTypeId && sst.IsActive)
                    .OrderBy(sst => sst.DisplayOrder)
                    .Select(sst => new SubServiceDto
                    {
                        SubServiceTypeId = sst.SubServiceTypeId,
                        ServiceType = sst.ServiceType,
                        Name = sst.Name,
                        Description = sst.Description,
                        DefaultDuration = sst.DefaultDuration
                    })
                    .ToListAsync();

                return Ok(subServices);
            }
            catch (Exception ex)
            {
                _logger.LogError(ex, "Error retrieving sub-service types for service type {ServiceTypeId}", serviceTypeId);
                return StatusCode(500, "An error occurred while retrieving sub-service types");
            }
        }

        /// <summary>
        /// Get all service types with their sub-services
        /// </summary>
        [HttpGet("service-types/with-sub-services")]
        [AllowAnonymous]
        public async Task<IActionResult> GetServiceTypesWithSubServices()
        {
            try
            {
                var serviceTypes = await _context.ServiceTypes
                    .Where(st => st.IsActive)
                    .OrderBy(st => st.DisplayOrder)
                    .Select(st => new
                    {
                        serviceTypeId = st.ServiceTypeId,
                        name = st.Name,
                        description = st.Description,
                        icon = st.Icon,
                        subServices = st.SubServiceTypes
                            .Where(sst => sst.IsActive)
                            .OrderBy(sst => sst.DisplayOrder)
                            .Select(sst => new SubServiceDto
                            {
                                SubServiceTypeId = sst.SubServiceTypeId,
                                ServiceType = sst.ServiceType,
                                Name = sst.Name,
                                Description = sst.Description,
                                DefaultDuration = sst.DefaultDuration
                            })
                            .ToList()
                    })
                    .ToListAsync();

                return Ok(serviceTypes);
            }
            catch (Exception ex)
            {
                _logger.LogError(ex, "Error retrieving service types with sub-services");
                return StatusCode(500, "An error occurred while retrieving service types");
            }
        }

        /// <summary>
        /// Test endpoint to verify API routing
        /// </summary>
        [HttpGet("test")]
        [AllowAnonymous]
        public IActionResult TestEndpoint()
        {
            _logger.LogInformation("Test endpoint called successfully");
            return Ok(new { message = "PetServices API is working", timestamp = DateTime.UtcNow });
        }

        /// <summary>
        /// Get full pricing information for a specific provider
        /// </summary>
        [HttpGet("providers/{userId}/full-pricing")]
        [AllowAnonymous]
        public async Task<IActionResult> GetProviderFullPricing(string userId)
        {
            try
            {
                _logger.LogInformation("GetProviderFullPricing called for user {UserId}", userId);

                // Get the pet service profile with related data
                var profile = await _context.PetServiceProfiles
                    .Include(p => p.User)
                    .Include(p => p.SubServices)
                        .ThenInclude(ss => ss.SubServiceType)
                    .Include(p => p.SubServices)
                        .ThenInclude(ss => ss.Rate)
                    .AsNoTracking()  // Add this for better performance
                    .FirstOrDefaultAsync(p => p.UserId == userId);

                if (profile == null)
                {
                    _logger.LogWarning("Provider profile not found for user {UserId}", userId);
                    return NotFound("Provider not found");
                }

                // Get unique service types from sub-services
                var serviceTypes = profile.SubServices
                    .Where(ss => ss.IsActive && ss.SubServiceType != null)
                    .Select(ss => ss.SubServiceType.ServiceType)
                    .Distinct()
                    .ToList();

                // Build service rates grouped by service type
                var serviceRates = new List<object>();

                foreach (var serviceTypeId in serviceTypes)
                {
                    var subServicesForType = profile.SubServices
                        .Where(ss => ss.IsActive && ss.SubServiceType?.ServiceType == serviceTypeId)
                        .ToList();

                    // Get rates for this service type
                    var hourlyRate = subServicesForType
                        .FirstOrDefault(ss => ss.Rate?.RateType == PetServiceRateType.Hourly)
                        ?.Rate?.Rate;

                    var dayRate = subServicesForType
                        .FirstOrDefault(ss => ss.Rate?.RateType == PetServiceRateType.Daily)
                        ?.Rate?.Rate;

                    var overnightRate = subServicesForType
                        .FirstOrDefault(ss => ss.Rate?.RateType == PetServiceRateType.Overnight)
                        ?.Rate?.Rate;

                    var flatRatePerDog = subServicesForType
                        .FirstOrDefault(ss => ss.Rate?.RateType == PetServiceRateType.PerDog)
                        ?.Rate?.Rate;

                    serviceRates.Add(new
                    {
                        serviceType = serviceTypeId,
                        serviceTypeName = GetServiceTypeName(serviceTypeId),
                        serviceTypeIcon = GetServiceTypeIcon(serviceTypeId),
                        hourlyRate = hourlyRate,
                        dayRate = dayRate,
                        overnightRate = overnightRate,
                        flatRatePerDog = flatRatePerDog,
                        notes = subServicesForType.FirstOrDefault()?.Description,
                        subServices = subServicesForType
                            .Where(ss => ss.Rate != null)
                            .Select(ss => new
                            {
                                subServiceType = ss.SubServiceTypeId,
                                subServiceName = ss.SubServiceType?.Name ?? "Additional Service",
                                description = ss.SubServiceType?.Description,
                                rate = ss.Rate?.Rate ?? 0,
                                isActive = ss.IsActive,
                                duration = ss.SubServiceType?.DefaultDuration
                            })
                            .ToList()
                    });
                }

                // Build comprehensive pricing response
                var fullPricing = new
                {
                    providerId = profile.UserId,
                    businessName = $"{profile.User?.FirstName} {profile.User?.LastName}".Trim(),
                    isFullData = true,
                    lastUpdated = profile.ModifiedDate,
                    serviceRates = serviceRates,
                    additionalCharges = new
                    {
                        weekendSurcharge = (decimal?)null, // Not available in current model
                        holidaySurcharge = (decimal?)null, // Not available in current model
                        emergencySurcharge = profile.EmergencySurchargePercent,
                        additionalDogCharge = profile.MultiPetDiscount, // This is actually a discount percentage
                        multiPetDiscount = profile.MultiPetDiscount
                    },
                    policies = new
                    {
                        cancellationPolicy = "Contact provider for cancellation policy", // Not available in current model
                        minimumBookingHours = (int?)null, // Not available in current model
                        maximumDogs = profile.MaxPetsPerBooking,
                        acceptsPuppies = profile.AcceptsSmallDogs,
                        acceptsSeniorDogs = profile.HandlesSeniorPets,
                        acceptsUntrainedDogs = profile.HandlesAnxiousPets,
                        // Additional pet size acceptance
                        acceptsSmallDogs = profile.AcceptsSmallDogs,
                        acceptsMediumDogs = profile.AcceptsMediumDogs,
                        acceptsLargeDogs = profile.AcceptsLargeDogs,
                        acceptsGiantDogs = profile.AcceptsGiantDogs,
                        maxDogWeight = profile.MaxDogWeight.HasValue 
                            ? Math.Round(profile.MaxDogWeight.Value / 2.20462m, 1) // Convert lbs to kg for display
                            : (decimal?)null,
                        // Medical capabilities
                        canAdministerMedication = profile.CanAdministerMedication,
                        canManageSpecialDiets = profile.CanManageSpecialDiets,
                        canAdministerInsulin = profile.CanAdministerInsulin,
                        // Emergency availability
                        available24x7 = profile.Available24x7,
                        offersEmergencyService = profile.OffersEmergencyService
                    }
                };

                return Ok(fullPricing);
            }
            catch (Exception ex)
            {
                _logger.LogError(ex, "Error retrieving full pricing for provider {UserId}", userId);
                return StatusCode(500, "An error occurred while retrieving pricing information");
            }
        }

        // Helper methods for service type names and icons
        private string GetServiceTypeName(int serviceTypeId)
        {
            return serviceTypeId switch
            {
                1 => "Dog Sitting",
                2 => "Dog Walking",
                3 => "Dog Training",
                4 => "Dog Grooming",
                5 => "Pet Taxi",
                6 => "Pet Photography",
                _ => "Pet Service"
            };
        }

        private string GetServiceTypeIcon(int serviceTypeId)
        {
            return serviceTypeId switch
            {
                1 => "fas fa-home",
                2 => "fas fa-walking",
                3 => "fas fa-graduation-cap",
                4 => "fas fa-cut",
                5 => "fas fa-car",
                6 => "fas fa-camera",
                _ => "fas fa-paw"
            };
        }

        // DUPLICATE METHOD COMMENTED OUT - This was causing Swagger generation to fail
        // The first SearchProviders method above should be used instead
        // TODO: Consolidate the two search methods into one

        ///// <summary>
        ///// Search for pet service providers
        ///// </summary>
        //[HttpGet("search")]
        //[AllowAnonymous]
        //public async Task<IActionResult> SearchProviders([FromQuery] SearchPetServicesRequest request)
        //{
        //    try
        //    {
        //        var result = await _petServiceService.SearchProvidersAsync(request);
        //        return Ok(result);
        //    }
        //    catch (Exception ex)
        //    {
        //        _logger.LogError(ex, "Error searching pet service providers");
        //        return StatusCode(500, "An error occurred while searching providers");
        //    }
        //}

        /// <summary>
        /// Search for pet service providers with combined availability data
        /// This endpoint consolidates search results and aggregate availability into a single response
        /// </summary>
        [HttpPost("search-with-availability")]
        [AllowAnonymous]
        public async Task<IActionResult> SearchProvidersWithAvailability([FromBody] SearchPetServicesRequest request)
        {
            try
            {
                _logger.LogInformation("SearchProvidersWithAvailability called with parameters: {@Request}", request);

                // Validate request
                if (request == null)
                {
                    _logger.LogWarning("SearchProvidersWithAvailability called with null request");
                    return BadRequest("Invalid request");
                }

                // Get combined results
                var result = await _petServiceService.SearchProvidersWithAvailabilityAsync(request);

                return Ok(result);
            }
            catch (Exception ex)
            {
                _logger.LogError(ex, "Error in SearchProvidersWithAvailability");
                return StatusCode(500, "An error occurred while searching providers");
            }
        }

        /// <summary>
        /// Get aggregate availability for search results
        /// </summary>
        [HttpGet("aggregate-availability")]
        [AllowAnonymous]
        public async Task<IActionResult> GetAggregateAvailability([FromQuery] SearchPetServicesRequest request)
        {
            try
            {
                _logger.LogInformation("GetAggregateAvailability called with parameters: {@Request}", request);

                // Validate request
                if (request == null)
                {
                    _logger.LogWarning("GetAggregateAvailability called with null request");
                    return BadRequest("Invalid request");
                }

                // Get the filtered providers based on search criteria
                SearchPetServicesResponse searchResult;
                try
                {
                    searchResult = await _petServiceService.SearchProvidersAsync(request);
                }
                catch (Exception ex)
                {
                    _logger.LogError(ex, "Error searching providers");
                    return Ok(new { dates = new List<object>(), totalProviders = 0, error = "Unable to search providers" });
                }

                if (searchResult?.Providers == null)
                {
                    _logger.LogWarning("SearchProvidersAsync returned null or null providers");
                    return Ok(new { dates = new List<object>(), totalProviders = 0 });
                }

                var providerIds = searchResult.Providers.Select(p => p.UserId).ToList();

                if (!providerIds.Any())
                {
                    return Ok(new { dates = new List<object>(), totalProviders = 0 });
                }

                // Get date range (default to next 30 days if not specified)
                var startDate = request.FromDate ?? DateTime.Today;
                var endDate = request.ToDate ?? DateTime.Today.AddDays(30);

                // Ensure date range is valid
                if (endDate < startDate)
                {
                    endDate = startDate.AddDays(30);
                }

                // Limit date range to prevent performance issues
                if ((endDate - startDate).TotalDays > 90)
                {
                    endDate = startDate.AddDays(90);
                }

                // Get availability for all filtered providers
                var availabilityData = new Dictionary<DateTime, int>();

                foreach (var providerId in providerIds)
                {
                    try
                    {
                        var availability = await _availabilityService.GetAvailabilityAsync(providerId, startDate, endDate);

                        if (availability != null)
                        {
                            foreach (var slot in availability)
                            {
                                if (slot == null) continue;

                                var date = slot.Date.Date;
                                if (slot.Status?.Equals("Available", StringComparison.OrdinalIgnoreCase) == true)
                                {
                                    if (availabilityData.ContainsKey(date))
                                    {
                                        availabilityData[date]++;
                                    }
                                    else
                                    {
                                        availabilityData[date] = 1;
                                    }
                                }
                            }
                        }
                    }
                    catch (Exception ex)
                    {
                        // Log error but continue with other providers
                        _logger.LogWarning(ex, "Error getting availability for provider {ProviderId}", providerId);
                    }
                }

                // Transform to response format
                var dates = availabilityData.Select(kvp => new
                {
                    date = kvp.Key.ToString("yyyy-MM-dd"),
                    availableProviders = kvp.Value,
                    status = kvp.Value > 0 ? "available" : "unavailable"
                }).ToList();

                // Add dates with no availability
                for (var date = startDate.Date; date <= endDate.Date; date = date.AddDays(1))
                {
                    if (!availabilityData.ContainsKey(date))
                    {
                        dates.Add(new
                        {
                            date = date.ToString("yyyy-MM-dd"),
                            availableProviders = 0,
                            status = "unavailable"
                        });
                    }
                }

                return Ok(new 
                { 
                    dates = dates.OrderBy(d => d.date),
                    totalProviders = providerIds.Count
                });
            }
            catch (Exception ex)
            {
                _logger.LogError(ex, "Unexpected error in GetAggregateAvailability");
                return Ok(new { dates = new List<object>(), totalProviders = 0, error = "An unexpected error occurred" });
            }
        }
    }
}

