using System.ComponentModel.DataAnnotations;
using MeAndMyDog.API.Models;
using MeAndMyDog.SharedKernel.Enums;

namespace MeAndMyDog.API.Entities.PetSitting;

/// <summary>
/// Extended profile information for users who are pet service providers
/// </summary>
public class PetServiceProfile
{
    public int PetServiceProfileId { get; set; }

    [Required]
    [MaxLength(450)]
    public string UserId { get; set; } = string.Empty;

    public Identity.ApplicationUser User { get; set; } = null!;

    [MaxLength(2000)]
    public string? Bio { get; set; }

    [MaxLength(2000)]
    public string? Experience { get; set; }

    [MaxLength(2000)]
    public string? Information { get; set; }

    [MaxLength(500)]
    public string? ServiceArea { get; set; }

    [Range(1, 50)]
    public int ServiceAreaRadius { get; set; } = 10; // Default to 10 miles

    // Location fields for Google Maps
    public double? Latitude { get; set; }

    public double? Longitude { get; set; }

    public bool IsActive { get; set; } = true;

    public DateTime CreatedDate { get; set; } = DateTime.UtcNow;

    public DateTime ModifiedDate { get; set; } = DateTime.UtcNow;

    // Alias for ModifiedDate to support both naming conventions
    public DateTime UpdatedAt
    {
        get => ModifiedDate;
        set => ModifiedDate = value;
    }

    // KYC Verification fields
    [MaxLength(50)]
    public string KycStatus { get; set; } = "NotStarted"; // NotStarted, Pending, InReview, Verified, Failed, Expired

    public DateTime? KycVerifiedDate { get; set; }

    public DateTime? KycExpiryDate { get; set; }

    [MaxLength(255)]
    public string? DiditId { get; set; } // Reusable Didit ID

    [MaxLength(255)]
    public string? KycSessionId { get; set; } // Current or last session ID

    [MaxLength(50)]
    public string? IdDocumentType { get; set; } // Passport, Driving License, National ID

    [MaxLength(100)]
    public string? IdDocumentNumber { get; set; } // Encrypted document number

    [MaxLength(50)]
    public string? BackgroundCheckStatus { get; set; } = "NotStarted"; // For future DBS checks

    public DateTime? BackgroundCheckDate { get; set; }

    [MaxLength(255)]
    public string? BackgroundCheckReference { get; set; }

    // Business License fields
    [MaxLength(100)]
    public string? BusinessLicenseNumber { get; set; }

    [MaxLength(50)]
    public string? BusinessLicenseType { get; set; } // e.g., "LLC", "Sole Proprietor", "Corporation"

    public bool BusinessLicenseVerified { get; set; } = false;

    public DateTime? BusinessLicenseVerifiedDate { get; set; }

    // Pet Size Acceptance
    public bool AcceptsSmallDogs { get; set; } = true;  // 0-11 kg (0-25 lbs)
    public bool AcceptsMediumDogs { get; set; } = true; // 12-27 kg (26-60 lbs)
    public bool AcceptsLargeDogs { get; set; } = true;  // 28-41 kg (61-90 lbs)
    public bool AcceptsGiantDogs { get; set; } = false; // 41+ kg (90+ lbs)

    /// <summary>
    /// Maximum dog weight in pounds (lbs). UI displays/accepts values in kg and converts.
    /// </summary>
    public decimal? MaxDogWeight { get; set; }

    // Medical Capabilities
    public bool CanAdministerMedication { get; set; } = false;
    public bool CanManageSpecialDiets { get; set; } = false;
    public bool CanAdministerInsulin { get; set; } = false;
    public bool HandlesAnxiousPets { get; set; } = true;
    public bool HandlesSeniorPets { get; set; } = true;

    // Multi-Pet Support
    [Range(1, 10)]
    public int MaxPetsPerBooking { get; set; } = 1;
    [Range(0, 100)]
    public decimal? MultiPetDiscount { get; set; }

    // Emergency Availability
    public bool Available24x7 { get; set; } = false;
    public bool OffersEmergencyService { get; set; } = false;
    [Range(0, 100)]
    public decimal? EmergencySurchargePercent { get; set; }

    // Experience Level (User-entered)
    [Range(0, 50)]
    public int? YearsOfExperience { get; set; }

    // Response Time Tracking
    public int? AverageResponseTimeMinutes { get; set; }
    public int? MaxResponseTimeMinutes { get; set; } 
    public DateTime? LastActiveDate { get; set; }

    // Insurance and Certifications
    public bool HasInsurance { get; set; } = false; 
    public string? Certifications { get; set; } 

    // Navigation properties - Updated to use new structure
    public ICollection<PetServiceSubService> SubServices { get; set; } = new List<PetServiceSubService>();
    public ICollection<PetServiceAvailability> Availabilities { get; set; } = new List<PetServiceAvailability>();
    public ICollection<Application> Applications { get; set; } = new List<Application>();
    public ICollection<PetServiceReview> Reviews { get; set; } = new List<PetServiceReview>();
    public ICollection<PetServiceImage> Images { get; set; } = new List<PetServiceImage>();

    // Helper properties for backward compatibility
    // Get rates from sub-services for specific service types
    public decimal GetHourlyRate(int serviceTypeId)
    {
        var subService = SubServices
            ?.Where(ss => ss.IsActive && ss.SubServiceType?.ServiceType == serviceTypeId)
            ?.FirstOrDefault(ss => ss.Rate?.RateType == PetServiceRateType.Hourly);

        return subService?.Rate?.Rate ?? 0;
    }

    public decimal GetDayRate(int serviceTypeId)
    {
        var subService = SubServices
            ?.Where(ss => ss.IsActive && ss.SubServiceType?.ServiceType == serviceTypeId)
            ?.FirstOrDefault(ss => ss.Rate?.RateType == PetServiceRateType.Daily);

        return subService?.Rate?.Rate ?? 0;
    }

    public decimal GetOvernightRate(int serviceTypeId)
    {
        var subService = SubServices
            ?.Where(ss => ss.IsActive && ss.SubServiceType?.ServiceType == serviceTypeId)
            ?.FirstOrDefault(ss => ss.Rate?.RateType == PetServiceRateType.Overnight);

        return subService?.Rate?.Rate ?? 0;
    }

    // Check if provider offers a specific service type
    public bool OffersServiceType(int serviceTypeId)
    {
        return SubServices?.Any(ss => ss.IsActive && ss.SubServiceType?.ServiceType == serviceTypeId) ?? false;
    }

    // Get all sub-services for a specific service type
    public IEnumerable<PetServiceSubService> GetSubServicesForType(int serviceTypeId)
    {
        return SubServices?.Where(ss => ss.IsActive && ss.SubServiceType?.ServiceType == serviceTypeId) 
            ?? Enumerable.Empty<PetServiceSubService>();
    }
}

