using System.ComponentModel.DataAnnotations;
using MeAndMyDog.SharedKernel.Enums;

namespace MeAndMyDog.API.Models.PetSitting
{
    /// <summary>
    /// Request model for searching pet service providers
    /// </summary>
    public class SearchPetServicesRequest
    {
        public string? Location { get; set; }
        public double? Latitude { get; set; }
        public double? Longitude { get; set; }

        [Range(1, 100)]
        public int RadiusInMiles { get; set; } = 10;

        public List<int> ServiceTypes { get; set; } = new List<int>();
        public List<int> SubServiceTypes { get; set; } = new List<int>();

        public DateTime? FromDate { get; set; }
        public DateTime? ToDate { get; set; }

        [Range(0, 10000)]
        public decimal? MinRate { get; set; }

        [Range(0, 10000)]
        public decimal? MaxRate { get; set; }

        public bool OnlyKycVerified { get; set; }

        public DateTime? SelectedDate { get; set; }

        public int? Radius { get; set; }

        public string SortBy { get; set; } = "Distance";

        [Range(1, 100)]
        public int PageNumber { get; set; } = 1;

        [Range(1, 100)]
        public int PageSize { get; set; } = 20;

        // Pet Size Filters
        public string? PetSize { get; set; } // "small", "medium", "large", "giant"

        /// <summary>
        /// Minimum weight in kilograms (from UI). Will be converted to lbs for database query.
        /// </summary>
        public decimal? MinWeight { get; set; }

        /// <summary>
        /// Maximum weight in kilograms (from UI). Will be converted to lbs for database query.
        /// </summary>
        public decimal? MaxWeight { get; set; }

        // Medical Capability Filters
        public bool? RequiresMedication { get; set; }
        public bool? RequiresSpecialDiet { get; set; }
        public bool? RequiresInsulin { get; set; }

        // Multi-Pet Filter
        [Range(1, 10)]
        public int? NumberOfPets { get; set; }

        // Emergency Availability Filters
        public bool? Requires24x7 { get; set; }
        public bool? RequiresEmergency { get; set; }

        // Experience Level Filter
        [Range(0, 50)]
        public int? MinimumExperience { get; set; } // Years of experience

        // Response Time Filter
        [Range(1, 10080)] // Max 1 week in minutes
        public int? MaxResponseTime { get; set; } // In minutes
    }

    /// <summary>
    /// Response model for pet service provider search
    /// </summary>
    public class SearchPetServicesResponse
    {
        public List<PetServiceProfileViewModel> Providers { get; set; } = new List<PetServiceProfileViewModel>();
        public int TotalCount { get; set; }
        public int PageNumber { get; set; }
        public int PageSize { get; set; }
        public int TotalPages { get; set; }
    }

    /// <summary>
    /// View model for pet service provider profile in search results
    /// </summary>
    public class PetServiceProfileViewModel
    {
        public string UserId { get; set; } = string.Empty;
        public string DisplayName { get; set; } = string.Empty;
        public string? ProfilePictureUrl { get; set; }
        public bool IsKycVerified { get; set; }
        public bool IsPremium { get; set; }
        public decimal HourlyRate { get; set; }
        public decimal? AverageRating { get; set; }
        public int ReviewCount { get; set; }
        public List<ServiceTypeViewModel> Services { get; set; } = new List<ServiceTypeViewModel>();
        public double? Distance { get; set; }
        public double? Latitude { get; set; }
        public double? Longitude { get; set; }

        // Additional profile details
        public string? Bio { get; set; }
        public string? Experience { get; set; }
        public int ServiceAreaRadius { get; set; }
        public string? Location { get; set; }  // Postcode/Location

        // Pet Size Acceptance
        public bool AcceptsSmallDogs { get; set; }
        public bool AcceptsMediumDogs { get; set; }
        public bool AcceptsLargeDogs { get; set; }
        public bool AcceptsGiantDogs { get; set; }

        /// <summary>
        /// Maximum dog weight. Stored in lbs in database, should be converted to kg for display.
        /// </summary>
        public decimal? MaxDogWeight { get; set; }

        // Medical Capabilities
        public bool CanAdministerMedication { get; set; }
        public bool CanManageSpecialDiets { get; set; }
        public bool CanAdministerInsulin { get; set; }
        public bool HandlesAnxiousPets { get; set; }
        public bool HandlesSeniorPets { get; set; }

        // Multi-Pet Support
        public int MaxPetsPerBooking { get; set; }
        public decimal? MultiPetDiscount { get; set; }

        // Emergency Availability
        public bool Available24x7 { get; set; }
        public bool OffersEmergencyService { get; set; }
        public decimal? EmergencySurchargePercent { get; set; }

        // Experience and Response Time
        public int? YearsOfExperience { get; set; }
        public int? AverageResponseTimeMinutes { get; set; }
        public DateTime? LastActiveDate { get; set; }
    }

    /// <summary>
    /// View model for service type in search results
    /// </summary>
    public class ServiceTypeViewModel
    {
        public int ServiceTypeId { get; set; }
        public string Name { get; set; } = string.Empty;
        public string? Icon { get; set; }
    }
}

