using System.ComponentModel.DataAnnotations;
using MeAndMyDog.API.Entities.PetSitting;
using MeAndMyDog.SharedKernel.Enums;

namespace MeAndMyDog.API.Models.PetSitting
{
    // DTOs for the controller
    public class EnableProviderModeDto
    {
        public string? Bio { get; set; }
        public string? Experience { get; set; }
        public string? Information { get; set; }

        [Range(1, 50)]
        public int ServiceAreaRadius { get; set; } = 10;

        [Required]
        public List<ServiceRateInputDto> ServiceRates { get; set; } = new List<ServiceRateInputDto>();

        // Medical Capabilities
        public bool CanAdministerMedication { get; set; }
        public bool HandlesSpecialDiets { get; set; }
        public bool CanAdministerInsulin { get; set; }
        public bool ExperiencedWithAnxiousPets { get; set; }
        public bool ExperiencedWithSeniorPets { get; set; }

        // Emergency & Availability
        public bool Available24x7 { get; set; }
        public bool OffersEmergencyServices { get; set; }
        public int? MaxResponseTimeMinutes { get; set; }

        // Pet Requirements
        public bool AcceptsSmallPets { get; set; }
        public bool AcceptsMediumPets { get; set; }
        public bool AcceptsLargePets { get; set; }
        public bool AcceptsGiantPets { get; set; }

        [Range(1, 10)]
        public int MaxPetsAtOnce { get; set; } = 1;

        // Qualifications
        public int YearsOfExperience { get; set; } = 0;
        public bool HasInsurance { get; set; }
        public string? Certifications { get; set; }
    }

    public class ServiceRateInputDto
    {
        [Required]
        public PetServiceType ServiceType { get; set; }

        // Rates based on service type
        // For Dog Sitter: all rates applicable
        // For Dog Walker & Dog Groomer: only FlatRatePerDog applicable
        [Range(5, 200)]
        public decimal? HourlyRate { get; set; }

        [Range(20, 500)]
        public decimal? DayRate { get; set; }

        [Range(50, 1000)]
        public decimal? OvernightRate { get; set; }

        [Range(10, 300)]
        public decimal? FlatRatePerDog { get; set; }

        // Sub-services for this service type
        public List<SubServiceInputDto>? SubServices { get; set; }
    }

    public class SubServiceInputDto
    {
        [Required]
        public int SubServiceTypeId { get; set; }

        public bool IsSelected { get; set; }

        [Range(5.00, 1000.00)]
        public decimal? Rate { get; set; }

        public PetServiceRateType RateType { get; set; } = PetServiceRateType.FlatRate;
    }

    public class UpdateProviderProfileDto
    {
        public string? Bio { get; set; }
        public string? Experience { get; set; }
        public string? Information { get; set; }

        [Range(1, 50)]
        public int? ServiceAreaRadius { get; set; }

        public bool? IsActive { get; set; }
        public List<ServiceRateInputDto>? ServiceRates { get; set; }
    }

    public class ProviderSearchParams
    {
        public string? Location { get; set; }
        public int? Radius { get; set; }
        public List<PetServiceType>? ServiceTypes { get; set; }
        public DateTime? StartDate { get; set; }
        public DateTime? EndDate { get; set; }
        public string? SortBy { get; set; }
        public int PageNumber { get; set; } = 1;
        public int PageSize { get; set; } = 10;
    }

    public class PetServiceProfileDto
    {
        public int Id { get; set; }
        public string UserId { get; set; } = string.Empty;
        public string DisplayName { get; set; } = string.Empty;
        public string? ProfilePictureUrl { get; set; }
        public string? Bio { get; set; }
        public string? Experience { get; set; }
        public string? Information { get; set; }
        public int ServiceAreaRadius { get; set; }
        public bool IsActive { get; set; }
        public bool IsKycVerified { get; set; }
        public List<ServiceRateDto> ServiceRates { get; set; } = new List<ServiceRateDto>();
        public decimal AverageRating { get; set; }
        public int ReviewCount { get; set; }
        public string? PostCode { get; set; }
        public DateTime CreatedDate { get; set; }

        // Pet Size Acceptance
        public bool AcceptsSmallDogs { get; set; }
        public bool AcceptsMediumDogs { get; set; }
        public bool AcceptsLargeDogs { get; set; }
        public bool AcceptsGiantDogs { get; set; }

        /// <summary>
        /// Maximum dog weight in kilograms (converted from lbs stored in database)
        /// </summary>
        public decimal? MaxDogWeight { get; set; }

        // Medical Capabilities
        public bool CanAdministerMedication { get; set; }
        public bool CanManageSpecialDiets { get; set; }
        public bool CanAdministerInsulin { get; set; }
        public bool HandlesAnxiousPets { get; set; }
        public bool HandlesSeniorPets { get; set; }

        // Multi-Pet Support
        public int MaxPetsPerBooking { get; set; }
        public decimal? MultiPetDiscount { get; set; }

        // Emergency Availability
        public bool Available24x7 { get; set; }
        public bool OffersEmergencyService { get; set; }
        public decimal? EmergencySurchargePercent { get; set; }

        // Experience and Response Time
        public int? YearsOfExperience { get; set; }
        public int? AverageResponseTimeMinutes { get; set; }
        public DateTime? LastActiveDate { get; set; }

        // Backward compatibility properties for transition period
        public decimal HourlyRate 
        { 
            get 
            {
                var sitterRate = ServiceRates?.FirstOrDefault(r => r.ServiceType == (int)PetServiceType.DogSitter);
                return sitterRate?.HourlyRate ?? 0;
            }
        }

        public decimal DayRate 
        { 
            get 
            {
                var sitterRate = ServiceRates?.FirstOrDefault(r => r.ServiceType == (int)PetServiceType.DogSitter);
                return sitterRate?.DayRate ?? 0;
            }
        }

        public decimal OvernightRate 
        { 
            get 
            {
                var sitterRate = ServiceRates?.FirstOrDefault(r => r.ServiceType == (int)PetServiceType.DogSitter);
                return sitterRate?.OvernightRate ?? 0;
            }
        }
    }

    public class ProviderStatisticsDto
    {
        public int CompletedJobs { get; set; }
        public int ActiveApplications { get; set; }
        public int TotalReviews { get; set; }
        public decimal AverageRating { get; set; }
        public decimal TotalEarnings { get; set; }
        public int UpcomingBookings { get; set; }
    }

    public class PagedResult<T>
    {
        public List<T> Items { get; set; } = new List<T>();
        public int TotalCount { get; set; }
        public int PageNumber { get; set; }
        public int PageSize { get; set; }
        public int TotalPages => (int)Math.Ceiling(TotalCount / (double)PageSize);
    }

}

