        public TimeSpan? StartTime { get; set; }

        public TimeSpan? EndTime { get; set; }

        public bool IsAvailable { get; set; } = true;

        public bool IsRecurring { get; set; }

        [StringLength(100)]
        public string? RecurrencePattern { get; set; }
    }

    /// <summary>
    /// DTO for bulk availability updates
    /// </summary>
    public class BulkUpdateAvailabilityDto
    {
        [Required]
        public List<AvailabilityUpdateDto> Availabilities { get; set; } = new List<AvailabilityUpdateDto>();
    }

    /// <summary>
    /// DTO for availability display
    /// </summary>
    public class AvailabilityDto
    {
        public int AvailabilityId { get; set; }
        public DateTime Date { get; set; }
        public TimeSpan? StartTime { get; set; }
        public TimeSpan? EndTime { get; set; }
        public bool IsAvailable { get; set; }
        public bool IsBooked { get; set; }
        public bool IsRecurring { get; set; }
        public string? RecurrencePattern { get; set; }
    }

    /// <summary>
    /// DTO for availability calendar display
    /// </summary>
    public class AvailabilityCalendarDto
    {
        public string PetServiceProviderId { get; set; } = string.Empty;
        public string PetServiceProviderName { get; set; } = string.Empty;
        public DateTime StartDate { get; set; }
        public DateTime EndDate { get; set; }
        public List<AvailabilityDateDto> Availabilities { get; set; } = new List<AvailabilityDateDto>();
        public List<BookedDateDto> BookedDates { get; set; } = new List<BookedDateDto>();
    }

    /// <summary>
    /// DTO for booked dates
    /// </summary>
    public class BookedDateDto
    {
        public DateTime Date { get; set; }
        public bool IsFullyBooked { get; set; }
        public string? BookingTitle { get; set; }
    }
}

