using System.ComponentModel.DataAnnotations;
using System.ComponentModel.DataAnnotations.Schema;

namespace MeAndMyDog.API.Entities.PetSitting
{
    /// <summary>
    /// Represents availability for a pet service provider
    /// </summary>
    [Table("PetServiceAvailabilities")]
    public class PetServiceAvailability
    {
        [Key]
        public int Id { get; set; }

        [Required]
        public int PetServiceProfileId { get; set; }

        [ForeignKey(nameof(PetServiceProfileId))]
        public PetServiceProfile PetServiceProfile { get; set; }

        [Required]
        public DateTime Date { get; set; }

        public TimeSpan? StartTime { get; set; }

        public TimeSpan? EndTime { get; set; }

        public bool IsRecurring { get; set; }

        [StringLength(100)]
        public string? RecurrencePattern { get; set; }

        [Required]
        public PetServiceAvailabilityStatusType Status { get; set; }

        public DateTime CreatedDate { get; set; }

        public DateTime? ModifiedDate { get; set; }
    }

    /// <summary>
    /// Availability status types
    /// </summary>
    public enum PetServiceAvailabilityStatusType
    {
        Available = 1,
        Booked = 2,
        Unavailable = 3,
        Pending = 4,
        Busy = 5
    }
}

using System.ComponentModel.DataAnnotations;

namespace MeAndMyDog.SharedKernel.Enums
{
    /// <summary>
    /// Types of pet services offered
    /// </summary>
    public enum PetServiceType
    {
        [Display(Name = "Dog Sitter")]
        DogSitter = 1,

        [Display(Name = "Dog Walker")]
        DogWalker = 2,

        [Display(Name = "Dog Groomer")]
        DogGroomer = 3
    }

    /// <summary>
    /// Sub-services within each main pet service type
    /// </summary>
    public enum PetServiceSubServiceType
    {
        // Dog Sitter sub-services (1-10)
        [Display(Name = "Overnight Stays")]
        OvernightStays = 1,

        [Display(Name = "Day Care")]
        DayCare = 2,

        [Display(Name = "Home Visits")]
        HomeVisits = 3,

        [Display(Name = "Feeding & Medication")]
        FeedingAndMedication = 4,

        // Dog Walker sub-services (11-20)
        [Display(Name = "30 Minute Walks")]
        ShortWalks30Min = 11,

        [Display(Name = "60 Minute Walks")]
        LongWalks60Min = 12,

        [Display(Name = "Park Visits")]
        ParkVisits = 13,

        [Display(Name = "Group Walks")]
        GroupWalks = 14,

        [Display(Name = "One-on-One Walks")]
        OneOnOneWalks = 15,

        [Display(Name = "Exercise Routines")]
        ExerciseRoutines = 16,

        // Dog Groomer sub-services (21-30)
        [Display(Name = "Basic Grooming")]
        BasicGrooming = 21,

        [Display(Name = "Full Grooming")]
        FullGrooming = 22,

        [Display(Name = "Nail Trimming")]
        NailTrimming = 23,

        [Display(Name = "Ear Cleaning")]
        EarCleaning = 24,

        [Display(Name = "Teeth Cleaning")]
        TeethCleaning = 25,

        [Display(Name = "Flea Treatment")]
        FleaTreatment = 26,

        [Display(Name = "De-Shedding")]
        DeShedding = 27,

        [Display(Name = "Hair Trimming")]
        HairTrimming = 28,

        [Display(Name = "Bathing & Drying")]
        BathingAndDrying = 29,

        [Display(Name = "Styling & Scissor Work")]
        StylingAndScissorWork = 30
    }

    /// <summary>
    /// Status of a pet sitting opportunity
    /// </summary>
    public enum OpportunityStatusType
    {
        Draft = 1,
        Active = 2,
        Filled = 3,
        Completed = 4,
        Cancelled = 5
    }

    /// <summary>
    /// Status of a pet sitting application
    /// </summary>
    public enum ApplicationStatusType
    {
        Pending = 1,
        Accepted = 2,
        Rejected = 3,
        Withdrawn = 4,
        Completed = 5
    }

    /// <summary>
    /// Availability status for pet sitters
    /// </summary>
    public enum AvailabilityStatusType
    {
        Available = 1,
        Busy = 2,
        PartiallyAvailable = 3,
        Holiday = 4
    }
}

