  1	using Microsoft.AspNetCore.Authorization;
  2	using Microsoft.AspNetCore.Mvc;
  3	using MeAndMyDog.WebApp.Models;
  4	using MeAndMyDog.SharedKernel.Enums;
  5	using MeAndMyDog.WebApp.Converters;
  6	using MeAndMyDog.WebApp.Helpers;
  7	using Newtonsoft.Json;
  8	using Newtonsoft.Json.Converters;
  9	using System.Net.Http.Headers;
 10	using System.Text;
 11	
 12	namespace MeAndMyDog.WebApp.Controllers
 13	{
 14	    [Authorize] // Changed from [Authorize(Roles = "PetServiceProvider")] to allow specific actions to have different requirements
 15	    public class PetServiceController : Controller
 16	    {
 17	        private readonly HttpClient _httpClient;
 18	        private readonly IHttpContextAccessor _httpContextAccessor;
 19	        private readonly ILogger<PetServiceController> _logger;
 20	        private readonly TokenHelper _tokenHelper;
 21	        
 22	        // JSON settings for deserializing service-related models with enum conversion
 23	        private readonly JsonSerializerSettings _serviceJsonSettings = new JsonSerializerSettings
 24	        {
 25	            Converters = new List<JsonConverter> 
 26	            { 
 27	                new StringEnumConverter(),
 28	                new PetServiceEnumConverter() 
 29	            }
 30	        };
 31	
 32	        public PetServiceController(
 33	            IHttpClientFactory httpClientFactory,
 34	            IHttpContextAccessor httpContextAccessor,
 35	            ILogger<PetServiceController> logger,
 36	            TokenHelper tokenHelper)
 37	        {
 38	            _httpClient = httpClientFactory.CreateClient("ApiGateway");
 39	            _httpContextAccessor = httpContextAccessor;
 40	            _logger = logger;
 41	            _tokenHelper = tokenHelper;
 42	        }
 43	
 44	        // Dashboard showing key metrics and recent activity
 45	        [Authorize(Roles = "PetServiceProvider")]
 46	        public async Task<IActionResult> Dashboard()
 47	        {
 48	            try
 49	            {
 50	                SetAuthorizationHeader();
 51	
 52	                // Check KYC status first
 53	                var kycResponse = await _httpClient.GetAsync("api/v1/petservices/my-kyc-status");
 54	                bool isKycVerified = false;
 55	
 56	                if (kycResponse.IsSuccessStatusCode)
 57	                {
 58	                    var kycJson = await kycResponse.Content.ReadAsStringAsync();
 59	                    dynamic kycStatus = JsonConvert.DeserializeObject(kycJson);
 60	                    isKycVerified = kycStatus?.isVerified ?? false;
 61	
 62	                    if (!isKycVerified)
 63	                    {
 64	                        // Redirect to verification if not verified
 65	                        TempData["Warning"] = "Please complete identity verification to access all features.";
 66	                        return RedirectToAction(nameof(Verification));
 67	                    }
 68	                }
 69	
 70	                // Get pet sitter profile
 71	                var profileResponse = await _httpClient.GetAsync("api/v1/PetServiceProviders/profile");
 72	                PetServiceProviderProfileViewModel profile = null;
 73	
 74	                if (profileResponse.IsSuccessStatusCode)
 75	                {
 76	                    var profileJson = await profileResponse.Content.ReadAsStringAsync();
 77	                    profile = JsonConvert.DeserializeObject<PetServiceProviderProfileViewModel>(profileJson, _serviceJsonSettings);
 78	                }
 79	
 80	                // Get recent applications
 81	                var applicationsResponse = await _httpClient.GetAsync("api/v1/applications/PetServiceProvider");
 82	                var applications = new List<PetServiceApplicationViewModel>();
 83	
 84	                if (applicationsResponse.IsSuccessStatusCode)
 85	                {
 86	                    var applicationsJson = await applicationsResponse.Content.ReadAsStringAsync();
 87	                    applications = JsonConvert.DeserializeObject<List<PetServiceApplicationViewModel>>(applicationsJson) ?? new List<PetServiceApplicationViewModel>();
 88	                }
 89	
 90	                // Get upcoming bookings
 91	                var upcomingApplications = applications
 92	                    .Where(a => a.Status == "Accepted" && a.StartDate >= DateTime.Today)
 93	                    .OrderBy(a => a.StartDate)
 94	                    .Take(5)
 95	                    .ToList();
 96	
 97	                // Get availability summary
 98	                var availabilityResponse = await _httpClient.GetAsync("api/v1/PetServiceProviders/availability");
 99	                var availability = new List<SimpleAvailabilityViewModel>();
100	
101	                if (availabilityResponse.IsSuccessStatusCode)
102	                {
103	                    var availabilityJson = await availabilityResponse.Content.ReadAsStringAsync();
104	                    var availabilityData = JsonConvert.DeserializeObject<List<dynamic>>(availabilityJson) ?? new List<dynamic>();
105	                    
106	                    // Map the API response to view models with the expected properties
107	                    availability = availabilityData.Select(a => new SimpleAvailabilityViewModel
108	                    {
109	                        Id = a.availabilityId ?? a.id ?? 0,
110	                        Date = a.date ?? DateTime.Now,
111	                        StartTime = a.startTime ?? TimeSpan.Zero,
112	                        EndTime = a.endTime ?? TimeSpan.Zero,
113	                        IsAvailable = a.isAvailable ?? true,
114	                        IsRecurring = a.isRecurring ?? false,
115	                        RecurrencePattern = a.recurrencePattern ?? "",
116	                        AvailabilityType = DetermineAvailabilityType(a.isAvailable ?? true, a.startTime, a.endTime),
117	                        Notes = GenerateAvailabilityNotes(a.isAvailable ?? true, a.isRecurring ?? false, a.recurrencePattern)
118	                    }).ToList();
119	                }
120	
121	                // Get user's display name
122	                string userDisplayName = User.Identity.Name; // Default to email
123	                try
124	                {
125	                    // Try to get user's actual name from the API
126	                    var userResponse = await _httpClient.GetAsync($"api/v1/users/me");
127	                    if (userResponse.IsSuccessStatusCode)
128	                    {
129	                        var userJson = await userResponse.Content.ReadAsStringAsync();
130	                        dynamic userData = JsonConvert.DeserializeObject(userJson);
131	                        
132	                        string firstName = userData?.firstName ?? "";
133	                        string lastName = userData?.lastName ?? "";
134	                        
135	                        if (!string.IsNullOrWhiteSpace(firstName) || !string.IsNullOrWhiteSpace(lastName))
136	                        {
137	                            userDisplayName = $"{firstName} {lastName}".Trim();
138	                        }
139	                    }
140	                }
141	                catch (Exception ex)
142	                {
143	                    _logger.LogWarning(ex, "Failed to get user display name");
144	                }
145	
146	                var viewModel = new PetServiceProviderDashboardViewModel
147	                {
148	                    Profile = profile,
149	                    UserDisplayName = userDisplayName,
150	                    TotalApplications = applications.Count,
151	                    PendingApplications = applications.Count(a => a.Status == "Pending"),
152	                    AcceptedApplications = applications.Count(a => a.Status == "Accepted"),
153	                    CompletedBookings = applications.Count(a => a.Status == "Accepted" && a.EndDate < DateTime.Today),
154	                    UpcomingBookings = upcomingApplications,
155	                    RecentApplications = applications.OrderByDescending(a => a.SubmittedAt).Take(5).ToList(),
156	                    Availability = availability,
157	                    IsKycVerified = isKycVerified
158	                };
159	
160	                // Calculate onboarding progress
161	                var onboardingTasks = new List<OnboardingTaskViewModel>();
162	                int completedTasks = 0;
163	
164	                // Profile completion
165	                bool profileCompleted = profile != null && !string.IsNullOrEmpty(profile.Bio) && !string.IsNullOrEmpty(profile.Experience);
166	                onboardingTasks.Add(new OnboardingTaskViewModel
167	                {
168	                    Title = "Complete Your Profile",
169	                    Description = "Add your bio and experience",
170	                    Icon = "fa-user",
171	                    IsCompleted = profileCompleted,
172	                    ActionUrl = Url.Action("Profile"),
173	                    ActionText = "Update Profile"
174	                });
175	                if (profileCompleted) completedTasks++;
176	
177	                // Profile photo
178	                bool photoUploaded = profile != null && !string.IsNullOrEmpty(profile.ProfilePicture) && !profile.ProfilePicture.Contains("default");
179	                onboardingTasks.Add(new OnboardingTaskViewModel
180	                {
181	                    Title = "Upload Profile Photo",
182	                    Description = "Add a professional photo",
183	                    Icon = "fa-camera",
184	                    IsCompleted = photoUploaded,
185	                    ActionUrl = Url.Action("Profile"),
186	                    ActionText = "Add Photo"
187	                });
188	                if (photoUploaded) completedTasks++;
189	
190	                // Services configuration
191	                bool servicesConfigured = profile != null && profile.ServiceRates != null && profile.ServiceRates.Any();
192	                onboardingTasks.Add(new OnboardingTaskViewModel
193	                {
194	                    Title = "Configure Services & Rates",
195	                    Description = "Set your service offerings and pricing",
196	                    Icon = "fa-list-check",
197	                    IsCompleted = servicesConfigured,
198	                    ActionUrl = Url.Action("ServiceRates"),
199	                    ActionText = "Set Rates"
200	                });
201	                if (servicesConfigured) completedTasks++;
202	
203	                // Availability
204	                bool availabilitySet = availability != null && availability.Any(a => a.IsAvailable);
205	                onboardingTasks.Add(new OnboardingTaskViewModel
206	                {
207	                    Title = "Set Your Availability",
208	                    Description = "Let pet owners know when you're free",
209	                    Icon = "fa-calendar",
210	                    IsCompleted = availabilitySet,
211	                    ActionUrl = Url.Action("AvailabilityCalendar"),
212	                    ActionText = "Set Schedule"
213	                });
214	                if (availabilitySet) completedTasks++;
215	
216	                // Identity verification
217	                onboardingTasks.Add(new OnboardingTaskViewModel
218	                {
219	                    Title = "Verify Your Identity",
220	                    Description = "Complete KYC verification for trust",
221	                    Icon = "fa-id-card",
222	                    IsCompleted = isKycVerified,
223	                    ActionUrl = Url.Action("Verification"),
224	                    ActionText = "Verify Now"
225	                });
226	                if (isKycVerified) completedTasks++;
227	
228	                // First booking
229	                bool firstBookingCompleted = applications.Any(a => a.Status == "Accepted" && a.EndDate < DateTime.Today);
230	                onboardingTasks.Add(new OnboardingTaskViewModel
231	                {
232	                    Title = "Complete Your First Booking",
233	                    Description = "Get your first 5-star review!",
234	                    Icon = "fa-star",
235	                    IsCompleted = firstBookingCompleted,
236	                    ActionUrl = Url.Action("BrowseOpportunities"),
237	                    ActionText = "Browse Jobs"
238	                });
239	                if (firstBookingCompleted) completedTasks++;
240	
241	                viewModel.OnboardingProgress = new OnboardingProgressViewModel
242	                {
243	                    CompletionPercentage = (int)Math.Round((double)completedTasks / onboardingTasks.Count * 100),
244	                    ProfileCompleted = profileCompleted,
245	                    ServicesConfigured = servicesConfigured,
246	                    AvailabilitySet = availabilitySet,
247	                    IdentityVerified = isKycVerified,
248	                    ProfilePhotoUploaded = photoUploaded,
249	                    FirstBookingCompleted = firstBookingCompleted,
250	                    Tasks = onboardingTasks
251	                };
252	
253	                // Get recent activities
254	                var recentActivities = new List<RecentActivityViewModel>();
255	
256	                // Add recent applications to activities
257	                foreach (var app in applications.OrderByDescending(a => a.SubmittedAt).Take(3))
258	                {
259	                    recentActivities.Add(new RecentActivityViewModel
260	                    {
261	                        Type = "application",
262	                        Title = $"Applied to: {app.OpportunityTitle}",
263	                        Description = $"Status: {app.Status}",
264	                        Timestamp = app.SubmittedAt,
265	                        Icon = "fa-file-alt",
266	                        IconColor = app.Status == "Accepted" ? "success" : app.Status == "Pending" ? "warning" : "secondary",
267	                        ActionUrl = Url.Action("ApplicationDetails", new { id = app.Id })
268	                    });
269	                }
270	
271	                viewModel.RecentActivities = recentActivities.OrderByDescending(a => a.Timestamp).Take(10).ToList();
272	
273	                // Get unread messages count from messaging API
274	                try
275	                {
276	                    var unreadResponse = await _httpClient.GetAsync($"api/v1/messages/unread");
277	                    if (unreadResponse.IsSuccessStatusCode)
278	                    {
279	                        var unreadJson = await unreadResponse.Content.ReadAsStringAsync();
280	                        dynamic unreadData = JsonConvert.DeserializeObject(unreadJson);
281	                        viewModel.UnreadMessages = unreadData?.totalUnread ?? 0;
282	                    }
283	                    else
284	                    {
285	                        viewModel.UnreadMessages = 0;
286	                    }
287	                }
288	                catch (Exception ex)
289	                {
290	                    _logger.LogWarning(ex, "Failed to get unread messages count");
291	                    viewModel.UnreadMessages = 0;
292	                }
293	
294	                // Get earnings data from invoicing/payment API
295	                try
296	                {
297	                    // Get pet service provider ID from profile
298	                    var petServiceProviderId = profile?.Id ?? 0;
299	                    
300	                    if (petServiceProviderId > 0)
301	                    {
302	                        // Get current month earnings
303	                        var currentMonthStart = new DateTime(DateTime.Now.Year, DateTime.Now.Month, 1);
304	                        var currentMonthEnd = currentMonthStart.AddMonths(1).AddDays(-1);
305	                        
306	                        var thisMonthResponse = await _httpClient.GetAsync(
307	                            $"api/v1/invoices/statistics?petServiceProviderId={petServiceProviderId}" +
308	                            $"&startDate={currentMonthStart:yyyy-MM-dd}&endDate={currentMonthEnd:yyyy-MM-dd}");
309	                        
310	                        if (thisMonthResponse.IsSuccessStatusCode)
311	                        {
312	                            var thisMonthJson = await thisMonthResponse.Content.ReadAsStringAsync();
313	                            dynamic thisMonthData = JsonConvert.DeserializeObject(thisMonthJson);
314	                            viewModel.ThisMonthEarnings = thisMonthData?.totalPaid ?? 0;
315	                        }
316	                        
317	                        // Get last month earnings
318	                        var lastMonthStart = currentMonthStart.AddMonths(-1);
319	                        var lastMonthEnd = currentMonthStart.AddDays(-1);
320	                        
321	                        var lastMonthResponse = await _httpClient.GetAsync(
322	                            $"api/v1/invoices/statistics?petServiceProviderId={petServiceProviderId}" +
323	                            $"&startDate={lastMonthStart:yyyy-MM-dd}&endDate={lastMonthEnd:yyyy-MM-dd}");
324	                        
325	                        if (lastMonthResponse.IsSuccessStatusCode)
326	                        {
327	                            var lastMonthJson = await lastMonthResponse.Content.ReadAsStringAsync();
328	                            dynamic lastMonthData = JsonConvert.DeserializeObject(lastMonthJson);
329	                            viewModel.LastMonthEarnings = lastMonthData?.totalPaid ?? 0;
330	                        }
331	                    }
332	                }
333	                catch (Exception ex)
334	                {
335	                    _logger.LogWarning(ex, "Failed to get earnings data");
336	                    viewModel.ThisMonthEarnings = 0;
337	                    viewModel.LastMonthEarnings = 0;
338	                }
339	
340	                // Get new reviews count from reviews API
341	                try
342	                {
343	                    var reviewsResponse = await _httpClient.GetAsync($"api/v1/reviews/new-count/this-month");
344	                    if (reviewsResponse.IsSuccessStatusCode)
345	                    {
346	                        var reviewsJson = await reviewsResponse.Content.ReadAsStringAsync();
347	                        dynamic reviewsData = JsonConvert.DeserializeObject(reviewsJson);
348	                        viewModel.NewReviewsCount = reviewsData?.count ?? 0;
349	                    }
350	                    else
351	                    {
352	                        viewModel.NewReviewsCount = 0;
353	                    }
354	                }
355	                catch (Exception ex)
356	                {
357	                    _logger.LogWarning(ex, "Failed to get new reviews count");
358	                    viewModel.NewReviewsCount = 0;
359	                }
360	
361	                return View(viewModel);
362	            }
363	            catch (Exception ex)
364	            {
365	                _logger.LogError(ex, "Error loading pet sitter dashboard");
366	                TempData["Error"] = "Error loading dashboard. Please try again.";
367	                return View(new PetServiceProviderDashboardViewModel());
368	            }
369	        }
370	
371	        // Profile management
372	        [Authorize(Roles = "PetServiceProvider")]
373	        public async Task<IActionResult> Profile()
374	        {
375	            try
376	            {
377	                SetAuthorizationHeader();
378	
379	                var response = await _httpClient.GetAsync("api/v1/PetServiceProviders/profile");
380	
381	                if (!response.IsSuccessStatusCode)
382	                {
383	                    if (response.StatusCode == System.Net.HttpStatusCode.NotFound)
384	                    {
385	                        // No profile exists, redirect to create
386	                        return RedirectToAction(nameof(CreateProfile));
387	                    }
388	
389	                    TempData["Error"] = "Error loading profile. Please try again.";
390	                    return RedirectToAction(nameof(Dashboard));
391	                }
392	
393	                var json = await response.Content.ReadAsStringAsync();
394	                var profile = JsonConvert.DeserializeObject<PetServiceProviderProfileViewModel>(json, _serviceJsonSettings);
395	
396	                return View(profile);
397	            }
398	            catch (Exception ex)
399	            {
400	                _logger.LogError(ex, "Error loading pet sitter profile");
401	                TempData["Error"] = "Error loading profile. Please try again.";
402	                return RedirectToAction(nameof(Dashboard));
403	            }
404	        }
405	
406	        // Create profile
407	        [Authorize(Roles = "PetServiceProvider")]
408	        public IActionResult CreateProfile()
409	        {
410	            return View(new CreatePetServiceProviderProfileViewModel());
411	        }
412	
413	        [HttpPost]
414	        [ValidateAntiForgeryToken]
415	        [Authorize(Roles = "PetServiceProvider")]
416	        public async Task<IActionResult> CreateProfile(CreatePetServiceProviderProfileViewModel model)
417	        {
418	            if (!ModelState.IsValid)
419	            {
420	                return View(model);
421	            }
422	
423	            try
424	            {
425	                SetAuthorizationHeader();
426	
427	                var json = JsonConvert.SerializeObject(model);
428	                var content = new StringContent(json, Encoding.UTF8, "application/json");
429	
430	                var response = await _httpClient.PostAsync("api/v1/PetServiceProviders/profile", content);
431	
432	                if (response.IsSuccessStatusCode)
433	                {
434	                    TempData["Success"] = "Profile created successfully!";
435	                    return RedirectToAction(nameof(Profile));
436	                }
437	
438	                var error = await response.Content.ReadAsStringAsync();
439	                ModelState.AddModelError("", $"Error creating profile: {error}");
440	            }
441	            catch (Exception ex)
442	            {
443	                _logger.LogError(ex, "Error creating pet sitter profile");
444	                ModelState.AddModelError("", "An error occurred while creating your profile.");
445	            }
446	
447	            return View(model);
448	        }
