using Microsoft.AspNetCore.Authorization;
using Microsoft.AspNetCore.Mvc;
using MeAndMyDog.WebApp.Models;
using MeAndMyDog.SharedKernel.Enums;
using MeAndMyDog.WebApp.Converters;
using MeAndMyDog.WebApp.Helpers;
using Newtonsoft.Json;
using Newtonsoft.Json.Converters;
using System.Net.Http.Headers;
using System.Text;

namespace MeAndMyDog.WebApp.Controllers
{
    [Authorize] // Changed from [Authorize(Roles = "PetServiceProvider")] to allow specific actions to have different requirements
    public class PetServiceController : Controller
    {
        private readonly HttpClient _httpClient;
        private readonly IHttpContextAccessor _httpContextAccessor;
        private readonly ILogger<PetServiceController> _logger;
        private readonly TokenHelper _tokenHelper;

        // JSON settings for deserializing service-related models with enum conversion
        private readonly JsonSerializerSettings _serviceJsonSettings = new JsonSerializerSettings
        {
            Converters = new List<JsonConverter> 
            { 
                new StringEnumConverter(),
                new PetServiceEnumConverter() 
            }
        };

        public PetServiceController(
            IHttpClientFactory httpClientFactory,
            IHttpContextAccessor httpContextAccessor,
            ILogger<PetServiceController> logger,
            TokenHelper tokenHelper)
        {
            _httpClient = httpClientFactory.CreateClient("ApiGateway");
            _httpContextAccessor = httpContextAccessor;
            _logger = logger;
            _tokenHelper = tokenHelper;
        }

        // Dashboard showing key metrics and recent activity
        [Authorize(Roles = "PetServiceProvider")]
        public async Task<IActionResult> Dashboard()
        {
            try
            {
                SetAuthorizationHeader();

                // Check KYC status first
                var kycResponse = await _httpClient.GetAsync("api/v1/petservices/my-kyc-status");
                bool isKycVerified = false;

                if (kycResponse.IsSuccessStatusCode)
                {
                    var kycJson = await kycResponse.Content.ReadAsStringAsync();
                    dynamic kycStatus = JsonConvert.DeserializeObject(kycJson);
                    isKycVerified = kycStatus?.isVerified ?? false;

                    if (!isKycVerified)
                    {
                        // Redirect to verification if not verified
                        TempData["Warning"] = "Please complete identity verification to access all features.";
                        return RedirectToAction(nameof(Verification));
                    }
                }

                // Get pet sitter profile
                var profileResponse = await _httpClient.GetAsync("api/v1/PetServiceProviders/profile");
                PetServiceProviderProfileViewModel profile = null;

                if (profileResponse.IsSuccessStatusCode)
                {
                    var profileJson = await profileResponse.Content.ReadAsStringAsync();
                    profile = JsonConvert.DeserializeObject<PetServiceProviderProfileViewModel>(profileJson, _serviceJsonSettings);
                }

                // Get recent applications
                var applicationsResponse = await _httpClient.GetAsync("api/v1/applications/PetServiceProvider");
                var applications = new List<PetServiceApplicationViewModel>();

                if (applicationsResponse.IsSuccessStatusCode)
                {
                    var applicationsJson = await applicationsResponse.Content.ReadAsStringAsync();
                    applications = JsonConvert.DeserializeObject<List<PetServiceApplicationViewModel>>(applicationsJson) ?? new List<PetServiceApplicationViewModel>();
                }

                // Get upcoming bookings
                var upcomingApplications = applications
                    .Where(a => a.Status == "Accepted" && a.StartDate >= DateTime.Today)
                    .OrderBy(a => a.StartDate)
                    .Take(5)
                    .ToList();

                // Get availability summary
                var availabilityResponse = await _httpClient.GetAsync("api/v1/PetServiceProviders/availability");
                var availability = new List<SimpleAvailabilityViewModel>();

                if (availabilityResponse.IsSuccessStatusCode)
                {
                    var availabilityJson = await availabilityResponse.Content.ReadAsStringAsync();
                    var availabilityData = JsonConvert.DeserializeObject<List<dynamic>>(availabilityJson) ?? new List<dynamic>();

                    // Map the API response to view models with the expected properties
                    availability = availabilityData.Select(a => new SimpleAvailabilityViewModel
                    {
                        Id = a.availabilityId ?? a.id ?? 0,
                        Date = a.date ?? DateTime.Now,
                        StartTime = a.startTime ?? TimeSpan.Zero,
                        EndTime = a.endTime ?? TimeSpan.Zero,
                        IsAvailable = a.isAvailable ?? true,
                        IsRecurring = a.isRecurring ?? false,
                        RecurrencePattern = a.recurrencePattern ?? "",
                        AvailabilityType = DetermineAvailabilityType(a.isAvailable ?? true, a.startTime, a.endTime),
                        Notes = GenerateAvailabilityNotes(a.isAvailable ?? true, a.isRecurring ?? false, a.recurrencePattern)
                    }).ToList();
                }

                // Get user's display name
                string userDisplayName = User.Identity.Name; // Default to email
                try
                {
                    // Try to get user's actual name from the API
                    var userResponse = await _httpClient.GetAsync($"api/v1/users/me");
                    if (userResponse.IsSuccessStatusCode)
                    {
                        var userJson = await userResponse.Content.ReadAsStringAsync();
                        dynamic userData = JsonConvert.DeserializeObject(userJson);

                        string firstName = userData?.firstName ?? "";
                        string lastName = userData?.lastName ?? "";

                        if (!string.IsNullOrWhiteSpace(firstName) || !string.IsNullOrWhiteSpace(lastName))
                        {
                            userDisplayName = $"{firstName} {lastName}".Trim();
                        }
                    }
                }
                catch (Exception ex)
                {
                    _logger.LogWarning(ex, "Failed to get user display name");
                }

                var viewModel = new PetServiceProviderDashboardViewModel
                {
                    Profile = profile,
                    UserDisplayName = userDisplayName,
                    TotalApplications = applications.Count,
                    PendingApplications = applications.Count(a => a.Status == "Pending"),
                    AcceptedApplications = applications.Count(a => a.Status == "Accepted"),
                    CompletedBookings = applications.Count(a => a.Status == "Accepted" && a.EndDate < DateTime.Today),
                    UpcomingBookings = upcomingApplications,
                    RecentApplications = applications.OrderByDescending(a => a.SubmittedAt).Take(5).ToList(),
                    Availability = availability,
                    IsKycVerified = isKycVerified
                };

                // Calculate onboarding progress
                var onboardingTasks = new List<OnboardingTaskViewModel>();
                int completedTasks = 0;

                // Profile completion
                bool profileCompleted = profile != null && !string.IsNullOrEmpty(profile.Bio) && !string.IsNullOrEmpty(profile.Experience);
                onboardingTasks.Add(new OnboardingTaskViewModel
                {
                    Title = "Complete Your Profile",
                    Description = "Add your bio and experience",
                    Icon = "fa-user",
                    IsCompleted = profileCompleted,
                    ActionUrl = Url.Action("Profile"),
                    ActionText = "Update Profile"
                });
                if (profileCompleted) completedTasks++;

                // Profile photo
                bool photoUploaded = profile != null && !string.IsNullOrEmpty(profile.ProfilePicture) && !profile.ProfilePicture.Contains("default");
                onboardingTasks.Add(new OnboardingTaskViewModel
                {
                    Title = "Upload Profile Photo",
                    Description = "Add a professional photo",
                    Icon = "fa-camera",
                    IsCompleted = photoUploaded,
                    ActionUrl = Url.Action("Profile"),
                    ActionText = "Add Photo"
                });
                if (photoUploaded) completedTasks++;

                // Services configuration
                bool servicesConfigured = profile != null && profile.ServiceRates != null && profile.ServiceRates.Any();
                onboardingTasks.Add(new OnboardingTaskViewModel
                {
                    Title = "Configure Services & Rates",
                    Description = "Set your service offerings and pricing",
                    Icon = "fa-list-check",
                    IsCompleted = servicesConfigured,
                    ActionUrl = Url.Action("ServiceRates"),
                    ActionText = "Set Rates"
                });
                if (servicesConfigured) completedTasks++;

                // Availability
                bool availabilitySet = availability != null && availability.Any(a => a.IsAvailable);
                onboardingTasks.Add(new OnboardingTaskViewModel
                {
                    Title = "Set Your Availability",
                    Description = "Let pet owners know when you're free",
                    Icon = "fa-calendar",
                    IsCompleted = availabilitySet,
                    ActionUrl = Url.Action("AvailabilityCalendar"),
                    ActionText = "Set Schedule"
                });
                if (availabilitySet) completedTasks++;

                // Identity verification
                onboardingTasks.Add(new OnboardingTaskViewModel
                {
                    Title = "Verify Your Identity",
                    Description = "Complete KYC verification for trust",
                    Icon = "fa-id-card",
                    IsCompleted = isKycVerified,
                    ActionUrl = Url.Action("Verification"),
                    ActionText = "Verify Now"
                });
                if (isKycVerified) completedTasks++;

                // First booking
                bool firstBookingCompleted = applications.Any(a => a.Status == "Accepted" && a.EndDate < DateTime.Today);
                onboardingTasks.Add(new OnboardingTaskViewModel
                {
                    Title = "Complete Your First Booking",
                    Description = "Get your first 5-star review!",
                    Icon = "fa-star",
                    IsCompleted = firstBookingCompleted,
                    ActionUrl = Url.Action("BrowseOpportunities"),
                    ActionText = "Browse Jobs"
                });
                if (firstBookingCompleted) completedTasks++;

                viewModel.OnboardingProgress = new OnboardingProgressViewModel
                {
                    CompletionPercentage = (int)Math.Round((double)completedTasks / onboardingTasks.Count * 100),
                    ProfileCompleted = profileCompleted,
                    ServicesConfigured = servicesConfigured,
                    AvailabilitySet = availabilitySet,
                    IdentityVerified = isKycVerified,
                    ProfilePhotoUploaded = photoUploaded,
                    FirstBookingCompleted = firstBookingCompleted,
                    Tasks = onboardingTasks
                };

                // Get recent activities
                var recentActivities = new List<RecentActivityViewModel>();

                // Add recent applications to activities
                foreach (var app in applications.OrderByDescending(a => a.SubmittedAt).Take(3))
                {
                    recentActivities.Add(new RecentActivityViewModel
                    {
                        Type = "application",
                        Title = $"Applied to: {app.OpportunityTitle}",
                        Description = $"Status: {app.Status}",
                        Timestamp = app.SubmittedAt,
                        Icon = "fa-file-alt",
                        IconColor = app.Status == "Accepted" ? "success" : app.Status == "Pending" ? "warning" : "secondary",
                        ActionUrl = Url.Action("ApplicationDetails", new { id = app.Id })
                    });
                }

                viewModel.RecentActivities = recentActivities.OrderByDescending(a => a.Timestamp).Take(10).ToList();

                // Get unread messages count from messaging API
                try
                {
                    var unreadResponse = await _httpClient.GetAsync($"api/v1/messages/unread");
                    if (unreadResponse.IsSuccessStatusCode)
                    {
                        var unreadJson = await unreadResponse.Content.ReadAsStringAsync();
                        dynamic unreadData = JsonConvert.DeserializeObject(unreadJson);
                        viewModel.UnreadMessages = unreadData?.totalUnread ?? 0;
                    }
                    else
                    {
                        viewModel.UnreadMessages = 0;
                    }
                }
                catch (Exception ex)
                {
                    _logger.LogWarning(ex, "Failed to get unread messages count");
                    viewModel.UnreadMessages = 0;
                }

                // Get earnings data from invoicing/payment API
                try
                {
                    // Get pet service provider ID from profile
                    var petServiceProviderId = profile?.Id ?? 0;

                    if (petServiceProviderId > 0)
                    {
                        // Get current month earnings
                        var currentMonthStart = new DateTime(DateTime.Now.Year, DateTime.Now.Month, 1);
                        var currentMonthEnd = currentMonthStart.AddMonths(1).AddDays(-1);

                        var thisMonthResponse = await _httpClient.GetAsync(
                            $"api/v1/invoices/statistics?petServiceProviderId={petServiceProviderId}" +
                            $"&startDate={currentMonthStart:yyyy-MM-dd}&endDate={currentMonthEnd:yyyy-MM-dd}");

                        if (thisMonthResponse.IsSuccessStatusCode)
                        {
                            var thisMonthJson = await thisMonthResponse.Content.ReadAsStringAsync();
                            dynamic thisMonthData = JsonConvert.DeserializeObject(thisMonthJson);
                            viewModel.ThisMonthEarnings = thisMonthData?.totalPaid ?? 0;
                        }

                        // Get last month earnings
                        var lastMonthStart = currentMonthStart.AddMonths(-1);
                        var lastMonthEnd = currentMonthStart.AddDays(-1);

                        var lastMonthResponse = await _httpClient.GetAsync(
                            $"api/v1/invoices/statistics?petServiceProviderId={petServiceProviderId}" +
                            $"&startDate={lastMonthStart:yyyy-MM-dd}&endDate={lastMonthEnd:yyyy-MM-dd}");

                        if (lastMonthResponse.IsSuccessStatusCode)
                        {
                            var lastMonthJson = await lastMonthResponse.Content.ReadAsStringAsync();
                            dynamic lastMonthData = JsonConvert.DeserializeObject(lastMonthJson);
                            viewModel.LastMonthEarnings = lastMonthData?.totalPaid ?? 0;
                        }
                    }
                }
                catch (Exception ex)
                {
                    _logger.LogWarning(ex, "Failed to get earnings data");
                    viewModel.ThisMonthEarnings = 0;
                    viewModel.LastMonthEarnings = 0;
                }

                // Get new reviews count from reviews API
                try
                {
                    var reviewsResponse = await _httpClient.GetAsync($"api/v1/reviews/new-count/this-month");
                    if (reviewsResponse.IsSuccessStatusCode)
                    {
                        var reviewsJson = await reviewsResponse.Content.ReadAsStringAsync();
                        dynamic reviewsData = JsonConvert.DeserializeObject(reviewsJson);
                        viewModel.NewReviewsCount = reviewsData?.count ?? 0;
                    }
                    else
                    {
                        viewModel.NewReviewsCount = 0;
                    }
                }
                catch (Exception ex)
                {
                    _logger.LogWarning(ex, "Failed to get new reviews count");
                    viewModel.NewReviewsCount = 0;
                }

                return View(viewModel);
            }
            catch (Exception ex)
            {
                _logger.LogError(ex, "Error loading pet sitter dashboard");
                TempData["Error"] = "Error loading dashboard. Please try again.";
                return View(new PetServiceProviderDashboardViewModel());
            }
        }

        // Profile management
        [Authorize(Roles = "PetServiceProvider")]
        public async Task<IActionResult> Profile()
        {
            try
            {
                SetAuthorizationHeader();

                var response = await _httpClient.GetAsync("api/v1/PetServiceProviders/profile");

                if (!response.IsSuccessStatusCode)
                {
                    if (response.StatusCode == System.Net.HttpStatusCode.NotFound)
                    {
                        // No profile exists, redirect to create
                        return RedirectToAction(nameof(CreateProfile));
                    }

                    TempData["Error"] = "Error loading profile. Please try again.";
                    return RedirectToAction(nameof(Dashboard));
                }

                var json = await response.Content.ReadAsStringAsync();
                var profile = JsonConvert.DeserializeObject<PetServiceProviderProfileViewModel>(json, _serviceJsonSettings);

                return View(profile);
            }
            catch (Exception ex)
            {
                _logger.LogError(ex, "Error loading pet sitter profile");
                TempData["Error"] = "Error loading profile. Please try again.";
                return RedirectToAction(nameof(Dashboard));
            }
        }

        // Create profile
        [Authorize(Roles = "PetServiceProvider")]
        public IActionResult CreateProfile()
        {
            return View(new CreatePetServiceProviderProfileViewModel());
        }

        [HttpPost]
        [ValidateAntiForgeryToken]
        [Authorize(Roles = "PetServiceProvider")]
        public async Task<IActionResult> CreateProfile(CreatePetServiceProviderProfileViewModel model)
        {
            if (!ModelState.IsValid)
            {
                return View(model);
            }

            try
            {
                SetAuthorizationHeader();

                var json = JsonConvert.SerializeObject(model);
                var content = new StringContent(json, Encoding.UTF8, "application/json");

                var response = await _httpClient.PostAsync("api/v1/PetServiceProviders/profile", content);

                if (response.IsSuccessStatusCode)
                {
                    TempData["Success"] = "Profile created successfully!";
                    return RedirectToAction(nameof(Profile));
                }

                var error = await response.Content.ReadAsStringAsync();
                ModelState.AddModelError("", $"Error creating profile: {error}");
            }
            catch (Exception ex)
            {
                _logger.LogError(ex, "Error creating pet sitter profile");
                ModelState.AddModelError("", "An error occurred while creating your profile.");
            }

            return View(model);
        }

        // New action for becoming a pet service provider with multiple services support
        [AllowAnonymous]
        public async Task<IActionResult> BecomeProvider()
        {
            // Check if user is already a provider
            if (User.IsInRole("PetServiceProvider"))
            {
                return RedirectToAction(nameof(Dashboard));
            }

            var model = new BecomePetServiceProviderWithRegistrationViewModel
            {
                ShowRegistrationFields = !User.Identity.IsAuthenticated,
                ServiceRates = new List<ServiceRateInputViewModel>()
            };

            try
            {
                // Get service types with sub-services from API
                var response = await _httpClient.GetAsync("api/v1/petservices/service-types/with-sub-services");

                if (response.IsSuccessStatusCode)
                {
                    var json = await response.Content.ReadAsStringAsync();
                    var serviceTypesData = JsonConvert.DeserializeObject<dynamic>(json);

                    foreach (var serviceType in serviceTypesData)
                    {
                        var serviceRateInput = new ServiceRateInputViewModel
                        {
                            ServiceType = (int)serviceType.serviceTypeId,
                            ServiceTypeName = (string)serviceType.name,
                            IsSelected = false,
                            SubServices = new List<SubServiceInputViewModel>()
                        };

                        foreach (var subService in serviceType.subServices)
                        {
                            serviceRateInput.SubServices.Add(new SubServiceInputViewModel
                            {
                                SubServiceType = (int)subService.SubServiceTypeId,
                                SubServiceName = (string)subService.Name,
                                Description = (string)subService.Description,
                                IsSelected = false
                            });
                        }

                        model.ServiceRates.Add(serviceRateInput);
                    }

                    _logger.LogInformation("Loaded {Count} service types from API for BecomeProvider", model.ServiceRates.Count);
