document.addEventListener('DOMContentLoaded', () => {
    // Game constants
    const COLS = 10;
    const ROWS = 20;
    const BLOCK_SIZE = 30;
    const EMPTY = 'white';

    // Game variables
    let board = [];
    let currentPiece;
    let gameInterval;
    let isPaused = false;
    let gameOver = false;
    let score = 0;
    let level = 1;
    let lines = 0;

    // DOM elements
    const gameBoard = document.getElementById('game-board');
    const scoreElement = document.getElementById('score');
    const levelElement = document.getElementById('level');
    const linesElement = document.getElementById('lines');
    const startButton = document.getElementById('start-button');
    const resetButton = document.getElementById('reset-button');

    // Tetromino shapes and colors
    const PIECES = [
        { shape: [[1, 1, 1, 1]], color: 'piece-I' }, // I
        { shape: [[1, 0, 0], [1, 1, 1]], color: 'piece-J' }, // J
        { shape: [[0, 0, 1], [1, 1, 1]], color: 'piece-L' }, // L
        { shape: [[1, 1], [1, 1]], color: 'piece-O' }, // O
        { shape: [[0, 1, 1], [1, 1, 0]], color: 'piece-S' }, // S
        { shape: [[0, 1, 0], [1, 1, 1]], color: 'piece-T' }, // T
        { shape: [[1, 1, 0], [0, 1, 1]], color: 'piece-Z' }  // Z
    ];

    // Initialize the game board
    function createBoard() {
        gameBoard.innerHTML = '';

        // Create the grid
        for (let row = 0; row < ROWS; row++) {
            board[row] = [];
            for (let col = 0; col < COLS; col++) {
                const cell = document.createElement('div');
                cell.classList.add('cell');
                cell.dataset.row = row;
                cell.dataset.col = col;
                gameBoard.appendChild(cell);
                board[row][col] = EMPTY;
            }
        }
    }

    // Reset the game
    function resetGame() {
        clearInterval(gameInterval);
        gameOver = false;
        isPaused = false;
        score = 0;
        level = 1;
        lines = 0;
        scoreElement.textContent = score;
        levelElement.textContent = level;
        linesElement.textContent = lines;
        createBoard();
        startButton.textContent = 'Start';
    }

    // Start the game
    function startGame() {
        if (gameOver) {
            resetGame();
        }

        if (isPaused) {
            isPaused = false;
            gameInterval = setInterval(moveDown, getGameSpeed());
            startButton.textContent = 'Pause';
        } else {
            isPaused = true;
            clearInterval(gameInterval);
            startButton.textContent = 'Resume';
        }

        if (!currentPiece) {
            createNewPiece();
            draw();
            gameInterval = setInterval(moveDown, getGameSpeed());
        }
    }

    // Get game speed based on level
    function getGameSpeed() {
        return 1000 - (level * 50);
    }

    // Create a random piece
    function createNewPiece() {
        const randomIndex = Math.floor(Math.random() * PIECES.length);
        const piece = PIECES[randomIndex];

        currentPiece = {
            shape: piece.shape,
            color: piece.color,
            row: 0,
            col: Math.floor(COLS / 2) - Math.floor(piece.shape[0].length / 2)
        };

        if (!isValidMove(0, 0)) {
            gameOver = true;
            clearInterval(gameInterval);
            alert('Game Over! Your score: ' + score);
        }
    }

    // Draw the board and current piece
    function draw() {
        // Clear the visual board
        const cells = document.querySelectorAll('.cell');
        cells.forEach(cell => {
            const row = parseInt(cell.dataset.row);
            const col = parseInt(cell.dataset.col);
            cell.className = 'cell';
            if (board[row][col] !== EMPTY) {
                cell.classList.add(board[row][col]);
            }
        });

        // Draw the current piece
        if (currentPiece) {
            for (let r = 0; r < currentPiece.shape.length; r++) {
                for (let c = 0; c < currentPiece.shape[r].length; c++) {
                    if (currentPiece.shape[r][c]) {
                        const row = currentPiece.row + r;
                        const col = currentPiece.col + c;
                        if (row >= 0) {
                            const cell = document.querySelector(`.cell[data-row="${row}"][data-col="${col}"]`);
                            if (cell) {
                                cell.classList.add(currentPiece.color);
                            }
                        }
                    }
                }
            }
        }
    }

    // Check if the move is valid
    function isValidMove(moveRow, moveCol) {
        for (let r = 0; r < currentPiece.shape.length; r++) {
            for (let c = 0; c < currentPiece.shape[r].length; c++) {
                if (currentPiece.shape[r][c]) {
                    const newRow = currentPiece.row + r + moveRow;
                    const newCol = currentPiece.col + c + moveCol;

                    if (newCol < 0 || newCol >= COLS || newRow >= ROWS) {
                        return false;
                    }

                    if (newRow >= 0 && board[newRow][newCol] !== EMPTY) {
                        return false;
                    }
                }
            }
        }
        return true;
    }

    // Rotate the current piece
    function rotatePiece() {
        if (isPaused || gameOver) return;

        const originalShape = currentPiece.shape;
        const rows = originalShape.length;
        const cols = originalShape[0].length;
        const rotated = [];

        // Create rotated shape
        for (let c = 0; c < cols; c++) {
            rotated[c] = [];
            for (let r = rows - 1; r >= 0; r--) {
                rotated[c].push(originalShape[r][c]);
            }
        }

        const originalPieceShape = currentPiece.shape;
        currentPiece.shape = rotated;

        // Check if rotation is valid, otherwise revert
        if (!isValidMove(0, 0)) {
            currentPiece.shape = originalPieceShape;
        }

        draw();
    }

    // Move the piece down
    function moveDown() {
        if (isPaused || gameOver) return;

        if (isValidMove(1, 0)) {
            currentPiece.row++;
            draw();
        } else {
            lockPiece();
            removeCompleteLines();
            createNewPiece();
            draw();
        }
    }

    // Lock the piece in place
    function lockPiece() {
        for (let r = 0; r < currentPiece.shape.length; r++) {
            for (let c = 0; c < currentPiece.shape[r].length; c++) {
                if (currentPiece.shape[r][c]) {
                    const row = currentPiece.row + r;
                    const col = currentPiece.col + c;
                    if (row >= 0) {
                        board[row][col] = currentPiece.color;
                    }
                }
            }
        }
    }

    // Remove complete lines
    function removeCompleteLines() {
        let linesCleared = 0;

        for (let r = ROWS - 1; r >= 0; r--) {
            let lineIsFull = true;

            for (let c = 0; c < COLS; c++) {
                if (board[r][c] === EMPTY) {
                    lineIsFull = false;
                    break;
                }
            }

            if (lineIsFull) {
                linesCleared++;

                // Move all lines above down
                for (let y = r; y > 0; y--) {
                    for (let c = 0; c < COLS; c++) {
                        board[y][c] = board[y-1][c];
                    }
                }

                // Clear the top line
                for (let c = 0; c < COLS; c++) {
                    board[0][c] = EMPTY;
                }

                // Move the check position back up
                r++;
            }
        }

        if (linesCleared > 0) {
            lines += linesCleared;
            score += linesCleared * 100 * level;

            // Level up every 10 lines
            level = Math.floor(lines / 10) + 1;

            scoreElement.textContent = score;
            levelElement.textContent = level;
            linesElement.textContent = lines;

            // Update game speed
            clearInterval(gameInterval);
            gameInterval = setInterval(moveDown, getGameSpeed());
        }
    }

    // Move the piece left or right
    function movePiece(direction) {
        if (isPaused || gameOver) return;

        const moveCol = direction === 'left' ? -1 : 1;

        if (isValidMove(0, moveCol)) {
            currentPiece.col += moveCol;
            draw();
        }
    }

    // Drop the piece to the bottom instantly
    function hardDrop() {
        if (isPaused || gameOver) return;

        let dropDistance = 0;

        while (isValidMove(dropDistance + 1, 0)) {
            dropDistance++;
        }

        if (dropDistance > 0) {
            currentPiece.row += dropDistance;
            draw();
            moveDown(); // Lock the piece and create a new one
        }
    }

    // Handle keyboard controls
    document.addEventListener('keydown', (e) => {
        if (gameOver) return;

        switch (e.key) {
            case 'ArrowLeft':
                movePiece('left');
                break;
            case 'ArrowRight':
                movePiece('right');
                break;
            case 'ArrowDown':
                moveDown();
                break;
            case 'ArrowUp':
                rotatePiece();
                break;
            case ' ':
                hardDrop();
                break;
            case 'p':
                startGame(); // Pause/Resume
                break;
        }
    });

    // Event listeners
    startButton.addEventListener('click', startGame);
    resetButton.addEventListener('click', resetGame);

    // Initialize the game
    resetGame();
});

